private void updateDesign()
{
    WPPWorkOrderTableInteractionHelper  interactionHelper;
    WPPWorkOrderStatusRegistration      registration;
    WPPWorkOrderStatusGroup             workOrderStatusGroup;
    WPPWorkOrderTaskTable               workOrderTaskTable;
    ;

    select firstOnly registration
    where registration.Status == workOrderLine.Status
        exists join workOrderStatusGroup
        where workOrderStatusGroup.WorkOrderStatusGroupId == registration.WorkOrderStatusGroupId
            join TableId from workOrderTaskTable
            where workOrderTaskTable.WorkOrderStatusGroupId == workOrderStatusGroup.WorkOrderStatusGroupId
            &&    workOrderTaskTable.TaskId                 == workOrderLine.TaskId;

    interactionHelper = WPPWorkOrderTableInteractionHelper::construct();
    interactionHelper.initLineActions(workOrderLine, registration);

    // 'update status' buttons
    WPPWorkOrderStatusUpdate_Created.enabled(interactionHelper.parmButtonStatusCreatedEnabled());
    WPPWorkOrderStatusUpdate_Planned.enabled(interactionHelper.parmButtonStatusPlannedEnabled());
    WPPWorkOrderStatusUpdate_Released.enabled(interactionHelper.parmButtonStatusReleasedEnabled());
    WPPWorkOrderStatusUpdate_InProgress.enabled(interactionHelper.parmButtonStatusInProgressEnabled());
    WPPWorkOrderStatusUpdate_Completed.enabled(interactionHelper.parmButtonStatusCompletedEnabled());
    WPPWorkOrderStatusUpdate_CheckedWorkshop.enabled(interactionHelper.parmButtonStatusCheckedWorkshopEnabled());
    WPPWorkOrderStatusUpdate_CheckedAdmin.enabled(interactionHelper.parmButtonStatusCheckedAdminEnabled());
    WPPWorkOrderStatusUpdate_Invoiced.enabled(interactionHelper.parmButtonStatusInvoicedEnabled());
    WPPWorkOrderStatusUpdate_Closed.enabled(interactionHelper.parmButtonStatusClosedEnabled());

    // 'journal' buttons
    ProjJournalTableEmpl.enabled(interactionHelper.parmButtonHourEntryEnabled());
    LedgerJournalTable7.enabled(interactionHelper.parmButtonExpenseEntryEnabled());
    InventJournalTableProject.enabled(interactionHelper.parmButtonItemJournalEntryEnabled());
    ProjJournalTableRevenue.enabled(interactionHelper.parmButtonFeeEntryEnabled());

    // 'item tasks' buttons
    // New
    ctrlProjItemReqForNew.enabled(interactionHelper.parmButtonProjItemReqEnabled());
    ctrlSalesOrderTableForNew.enabled(interactionHelper.parmButtonSalesOrderTableEnabled());
    ctrlPurchOrderTableForNew.enabled(interactionHelper.parmButtonPurchOrderTableEnabled());
    ctrlProdTableForNew.enabled(interactionHelper.parmButtonProdTableEnabled());
    ctrlPurchReqTableForNew.enabled(interactionHelper.parmButtonPurchReqTableEnabled());
    ctrlPurchRFQCaseTableForNew.enabled(interactionHelper.parmButtonPurchRFQCaseTableEnabled());
    ctrlSalesAgreementDetailsForNew.enabled(interactionHelper.parmButtonSalesAgreementDetailsEnabled());
    ctrlPurchAgreementDetailsForNew.enabled(interactionHelper.parmButtonPurchAgreementDetailsEnabled());

    // Edit
    ProjSalesItemReqForEdit.enabled(interactionHelper.parmButtonProjItemReqEditEnabled());
    WPPWorkOrderSalesTableForEdit.enabled(interactionHelper.parmButtonSalesOrderTableEditEnabled());
    WPPWorkOrderPurchTableForEdit.enabled(interactionHelper.parmButtonPurchOrderTableEditEnabled());
    ProjProdTableForEdit.enabled(interactionHelper.parmButtonProdTableEditEnabled());
    PurchReqTableForEdit.enabled(interactionHelper.parmButtonPurchReqTableEditEnabled());
    PurchRFQCaseTableForEdit.enabled(interactionHelper.parmButtonPurchRFQCaseTableEditEnabled());
    ProjSalesAgreementForEdit.enabled(interactionHelper.parmButtonSalesAgreementDetEditEnabled());
    ProjPurchAgreementForEdit.enabled(interactionHelper.parmButtonPurchAgreementDetEditEnabled());

    // 'Other palces' buttons
    WPPViewDetails.enabled(WorkOrderTable.OrderTableId && WorkOrderTable.OrderRecId);
}

private void updateDesign()
{
    container                       con;
    SalesTableCheckIfUpdateContract checkIfUpdateSales;
    SalesTableCanBeUpdatedContract  canBeUpdatedSales;
    PurchTableCheckIfUpdateContract checkIfUpdatePurch;
    PurchTableCanBeUpdatedContract  canBeUpdatedPurch;
    SalesTableInteractionHelper     salesHelper;
    PurchTableInteractionHelper     purchHelper;
    ;

    if(rentalHeader)
    {
        if(orderAccountType == CustVendACType::Cust)
        {
            // create objects
            checkIfUpdateSales = SalesTableCheckIfUpdateContract::construct();
            checkIfUpdateSales.parmCheckPickingList(true);
            checkIfUpdateSales.parmCheckPickingListRegistration(true);
            checkIfUpdateSales.parmCheckPackingSlip(true);
            checkIfUpdateSales.RORparmCheckReturnNote(true);

            con = rentalHeader.checkIfUpdate(checkIfUpdateSales.pack());
            canBeUpdatedSales = SalesTableCanBeUpdatedContract::create(con);
            salesHelper = SalesTableInteractionHelper::construct();
            salesHelper.initHeaderActions(rentalHeader);

            // 'pro forma' buttons
            buttonProformaReceiptsList.visible(false);
            buttonProformaPackingSlip.enabled(canBeUpdatedSales.parmCanPackingSlipBeUpdated());

            // 'generate' buttons
            buttonUpdatePickingList.visible(true);
            buttonUpdatePickingList.enabled(canBeUpdatedSales.parmCanPickingListBeUpdated());
            buttonUpdateReceiptsList.visible(false);
            buttonUpdatePickingListRegistrate.visible(true);
            buttonUpdatePickingListRegistrate.enabled(canBeUpdatedSales.parmCanPickingListBeRegistrated());
            buttonUpdatePackingSlip.enabled(canBeUpdatedSales.parmCanPackingSlipBeUpdated());
            buttonUpdateReturnNote.visible(SalesTable::find(rentalHeader.HeaderId).SalesType == SalesType::RORRental);
            buttonUpdateReturnNote.enabled(canBeUpdatedSales.RORparmCanReturnNoteBeUpdated());

            // 'print original' buttons
            buttonJournalPickingList.visible(true);
            buttonJournalPickingList.enabled(salesHelper.parmExistsPickingList());
            buttonJournalReceiptsList.visible(false);
            buttonJournalPackingSlip.enabled(salesHelper.parmExistsPackingSlip());
            buttonJournalReturnNote.visible(buttonUpdateReturnNote.visible());
            buttonJournalReturnNote.enabled(salesHelper.RORparmExistsReturned());
        }
        else
        {
            // create objects
            checkIfUpdatePurch = PurchTableCheckIfUpdateContract::construct();
            checkIfUpdatePurch.parmCheckReceiptsList(true);
            checkIfUpdatePurch.parmCheckPackingSlip(true);

            con = rentalHeader.checkIfUpdate(checkIfUpdatePurch.pack());
            canBeUpdatedPurch = PurchTableCanBeUpdatedContract::create(con);
            purchHelper = PurchTableInteractionHelper::construct(0, rentalHeader);

            // 'pro forma' buttons
            buttonProformaReceiptsList.visible(true);
            buttonProformaReceiptsList.enabled(canBeUpdatedPurch.parmCanReceiptsListBeUpdated());
            buttonProformaPackingSlip.enabled(canBeUpdatedPurch.parmCanPackingSlipBeUpdated());

            // 'generate' buttons
            buttonUpdatePickingList.visible(false);
            buttonUpdatePickingListRegistrate.visible(false);
            buttonUpdateReceiptsList.visible(true);
            buttonUpdateReceiptsList.enabled(canBeUpdatedPurch.parmCanReceiptsListBeUpdated());
            buttonUpdatePackingSlip.enabled(canBeUpdatedPurch.parmCanPackingSlipBeUpdated());
            buttonUpdateReturnNote.visible(false);

            // 'print original' buttons
            buttonJournalPickingList.visible(false);
            buttonJournalReceiptsList.visible(true);
            buttonJournalReceiptsList.enabled(purchHelper.parmenableReceiptsListButton());
            buttonJournalPackingSlip.enabled(purchHelper.parmenablePackingSlipButton());
            buttonJournalReturnNote.visible(false);
        }
    }
    else
    {
        buttonProformaReceiptsList.enabled(false);
        buttonProformaPackingSlip.enabled(false);

        buttonUpdatePickingList.enabled(false);
        buttonUpdateReceiptsList.enabled(false);
        buttonUpdatePickingListRegistrate.enabled(false);
        buttonUpdatePackingSlip.enabled(false);
        buttonUpdateReturnNote.enabled(false);

        buttonJournalPickingList.enabled(false);
        buttonJournalReceiptsList.enabled(false);
        buttonJournalPackingSlip.enabled(false);
        buttonJournalReturnNote.enabled(false);
    }
}

private void retrieveOrderInfo()
{
    SalesTable  salesTable;
    PurchTable  purchTable;
    ;

    if(workOrderTable.OrderRecId)
    {
        switch(workOrderTable.OrderTableId)
        {
            case tableNum(SalesLine):
                rentalLine = SalesLine::findRecId(workOrderTable.OrderRecId);
                orderAccountType = CustVendACType::Cust;

                if(rentalLine)
                {
                    salesTable = rentalLine.RORGetHeader();
                    rentalHeader = salesTable;

                    if(salesTable.SalesType == SalesType::RORRental) // additional notes only exist on rental orders
                    {
                        additionalOrderNotes = DNRAdditionalNoteTable::find(salesTable.TableId, salesTable.RecId);
                    }
                }
                break;

            case tableNum(SalesTable):
                salesTable = SalesTable::findRecId(workOrderTable.OrderRecId);
                rentalHeader = salesTable;
                orderAccountType = CustVendACType::Cust;

                if(salesTable.SalesType == SalesType::RORRental) // additional notes only exist on rental orders
                {
                    additionalOrderNotes = DNRAdditionalNoteTable::find(salesTable.TableId, salesTable.RecId);
                }
                break;

            case tableNum(PurchLine):
                rentalLine = PurchLine::findRecId(workOrderTable.OrderRecId);
                orderAccountType = CustVendACType::Vend;

                if(rentalLine)
                {
                    rentalHeader = rentalLine.RORGetHeader();
                }
                break;

            case tableNum(PurchTable):
                purchTable = PurchTable::findRecId(workOrderTable.OrderRecId);
                rentalHeader = purchTable;
                orderAccountType = CustVendACType::Vend;
                break;

            default:
                throw error(strFmt("@SYS31187", tableId2name(workOrderTable.OrderTableId)));
        }
    }
}

public int active()
{
    int ret;
    ;

    ret = super();
    this.retrieveOrderInfo();
    this.updateDesign();

    return ret;
}

