public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbdsLedgerJournalLink;
    WPPParameters           parameters;

    parameters = WPPParameters::find();
    query = this.query();

    if (parameters.SeparateProjectPerWorkOrderLine)
    {
        qbdsLedgerJournalLink = query.dataSourceTable(tableNum(WPPWorkOrderLineProjJournalLink));
        SysQuery::findOrCreateRange(qbdsLedgerJournalLink, fieldNum(WPPWorkOrderLineProjJournalLink, WorkOrderLineRecId)).value(SysQuery::value(workOrderLine.RecId));
    }
    else
    {
        qbdsLedgerJournalLink = query.dataSourceTable(tableNum(WPPWorkOrderLineHourTransLink));
        SysQuery::findOrCreateRange(qbdsLedgerJournalLink, fieldNum(WPPWorkOrderLineHourTransLink, WorkOrderLineRecId)).value(SysQuery::value(workOrderLine.RecId));
    }


    super();
}

public void init()
{
    QueryBuildDataSource    qbdsProjJournalTrans, qbdsLedgerJournalLink;
    WPPParameters           parameters;

    super();

    parameters = WPPParameters::find();
    qbdsProjJournalTrans = this.query().dataSourceTable(tableNum(ProjJournalTrans));

    if (parameters.SeparateProjectPerWorkOrderLine)
    {
        qbdsLedgerJournalLink = qbdsProjJournalTrans.addDataSource(tableNum(WPPWorkOrderLineProjJournalLink));
        qbdsLedgerJournalLink.addLink(fieldNum(ProjJournalTrans, JournalId), fieldNum(WPPWorkOrderLineProjJournalLink, ProjJournalId));
        qbdsLedgerJournalLink.joinMode(JoinMode::ExistsJoin);
    }
    else
    {
        qbdsLedgerJournalLink = qbdsProjJournalTrans.addDataSource(tableNum(WPPWorkOrderLineHourTransLink));
        qbdsLedgerJournalLink.relations(true);
        qbdsLedgerJournalLink.joinMode(JoinMode::ExistsJoin);
    }
}

