public void setTmpData()
{
    WPPTaskRequirementsData dataClass;

    dataClass = WPPTaskRequirementsData::construct(WorkOrderLine);

    TaskRequirements_ds.queryBuildDataSource().clearDynalinks();
    // BP Deviation documented - DynaRent
    TaskRequirements.setTmp();
    TaskRequirements.setTmpData(dataClass.populateTmpData(WorkOrderLine.TaskId));

    TaskRequirements_ds.research();
}

