public void jumpRefCustVendAccount()
{
    if (WorkOrderTable.OrganizationParty)
    {
        if (WorkOrderTable.CustVendAccountType == CustVendACType::Cust)
        {
            DNRUtilClass::crossCompanyJumpRef(WorkOrderTable.CompanyId, tableNum(CustTable), int642Str(WorkOrderTable.OrganizationParty));
        }
        else
        {
            DNRUtilClass::crossCompanyJumpRef(WorkOrderTable.CompanyId, tableNum(VendTable), int642Str(WorkOrderTable.OrganizationParty));
        }
    }
}

[SuppressBPWarningAttribute("BPCheckInvalidInitFormMethodWarning", "Validation of caller must be performed before invoking super()")]
public void init()
{
    DimensionEnumeration    editableDimensionSet;
    BOBObjectView           objectView;
    FormRun                 callerForm;
    RORRentalOrderLineView  rentalOrderLineView;
    Args                    args;
    WPPWorkOrderTable       workOrderTableBuffer;

    workOrderTableForm = WPPWorkOrderTableForm::construct();

    args = element.args();
    // When called form an infoPart the WPPWorkOrderView is passed in
    // but it is not a datasource on the form,
    // therefore the lookup* properties need to modified
    // Used HcmBenefitSetup as an example
    if (args.lookupTable() == tableNum(WPPWorkOrderView))
    {
        if (args.lookupField() == fieldNum(WPPWorkOrderView, WorkOrderId))
        {
            workOrderTableBuffer = WPPWorkOrderTable::find(element.args().lookupValue());

            element.args().lookupTable(tableNum(WPPWorkOrderTable));
            element.args().lookupField(fieldNum(WPPWorkOrderTable, WorkOrderId));
            element.args().lookupValue(workOrderTableBuffer.WorkOrderId);
        }
    }

   super(); 

    //isDocuActionTemplate = DocuActionForTemplate::createTemplateOnMenuButton(mbTemplatesButton, WorkOrderTable.TableId);

    if (args.menuItemName() == menuitemDisplayStr(WPPWorkOrderTableNew))
    {
        WorkOrderTable_q.dataSourceTable(tableNum(WPPWorkOrderTable)).clearDynalinks();
    }

    if (TradeFormHelper::isCalledFromForm(args, formStr(WPPWorkOrderTableListPage)))
    {
        callerForm = args.caller();

        switch (callerForm.args().dataset())
        {
            case tableNum(BOBObjectView):
                objectView = callerForm.args().record();
                workOrderTableForm.BOBparmObjectTableRecId(objectView.RecId);
                break;

            case tableNum(CustTable),
                 tableNum(VendTable):
                workOrderTableForm.parmCommon(callerForm.args().record());
                break;
        }
    }
    else
    {
        switch (args.dataset())
        {
            case tableNum(ProjTable),
                 tableNum(SalesLine):
                workOrderTableForm.parmCommon(args.record());
                break;

            case tableNum(RORRentalOrderLineView):
                rentalOrderLineView = args.record();
                workOrderTableForm.BOBparmObjectTableRecId(BOBObjectTable::findInventSerialId(rentalOrderLineView.inventSerialId).RecId);
                break;
        }
    }

    FINDimValueController::setEditableDimensionSet(tableNum(WPPWorkOrderTable), DimensionEntryControlHeader);
    FINDimValueController::setEditableDimensionSet(tableNum(WPPWorkOrderLine), DimensionEntryControlLine);

    element.RORUpdateDesign();

    LogisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    LogisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
}

public void RORUpdateDesign()
{
    AdditionalLineNotes.enabled(LineView.isActivePage() && !TabPageGrid.isActivePage());
}

public void run()
{
    SalesLine       salesLine;
    ProjInvoiceJour projInvoiceJour;

    super();

    //docuActionTemplateUndefined.visible(!isDocuActionTemplate);

    switch (element.args().dataset())
    {
        case tableNum(SalesTable),
             tableNum(PurchTable),
             tableNum(BOBObjectView):
            element.task(1317); // switch to grid view
            break;

        case tableNum(SalesLine):
            salesLine = element.args().record();

            if ((select count(RecId) from WPPWorkOrderTable
                 where WPPWorkOrderTable.OrderTableId == tableNum(SalesLine)
                 &&    WPPWorkOrderTable.OrderRecId   == salesLine.RecId).RecId > 1)
            {
                element.task(1317); // switch to grid view
            }
            break;

        case tableNum(ProjInvoiceJour):
            projInvoiceJour = element.args().record();

            if ((select count(RecId) from WPPWorkOrderTable
                 where WPPWorkOrderTable.ProjInvoiceProjId == projInvoiceJour.ProjInvoiceProjId).RecId > 1)
            {
                element.task(1317); // switch to grid view
            }
            else
            {
                element.task(1316); // switch to details view
            }
            break;

        default:
            break;
    }
}

