public void BSAUpdateWithActiveContract()
{
    BSAContractTable contractTable = BSAContractTable::findActiveContract(smmBusRelTable::findByParty(WorkOrderTable.OrganizationParty).BusRelAccount, WorkOrderTable.StartDate, WorkOrderTable.EndDate);
    ;

    //Active contract will always be highest contract, but we need to ensure that it is a customer type contract.
    if (contractTable.BusRelType == TableGroupAll::Table)
    {
        WorkOrderTable.ProjInvoiceProjId = contractTable.ProjInvoiceProjId;
    }
}

public void init()
{
    QueryBuildDataSource    qbdsWorkOrderTable;
    QueryBuildDataSource    qbdsOrderLine;
    QueryBuildRange         qbr;

    ProjTable               projTable;
    ProjCostTrans           projCostTrans;
    ProjEmplTrans           projEmplTrans;
    ProjItemTrans           projItemTrans;
    ProjRevenueTrans        projRevenueTrans;
    ProjInvoiceJour         projInvoiceJour;

    void filterOnProjTable(ProjTable _projTable)
    {
        qbdsOrderLine = qbdsWorkOrderTable.addDataSource(tableNum(WPPWorkOrderLine));
        qbdsOrderLine.relations(false);
        qbdsOrderLine.joinMode(JoinMode::ExistsJoin);
        qbdsOrderLine.addLink(fieldNum(WPPWorkOrderTable, WorkOrderId), fieldNum(WPPWorkOrderLine, WorkOrderId));
        qbr = qbdsOrderLine.addRange(fieldNum(WPPWorkOrderLine, DataAreaId));
        qbr.value(strFmt('((%1.%2 == "%3") || ((%4.%5 == %6) && (%4.%7 == %8)))',
                        qbdsOrderLine.name(),
                        fieldStr(WPPWorkOrderLine, ProjId),
                        _projTable.ProjId,
                        qbdsWorkOrderTable.name(),
                        fieldStr(WPPWorkOrderTable, OrderTableId),
                        _projTable.TableId,
                        fieldStr(WPPWorkOrderTable, OrderRecId),
                        _projTable.RecId));
    }

    super();

    qbdsWorkOrderTable = WorkOrderTable_ds.query().dataSourceTable(tableNum(WPPWorkOrderTable));
    qbdsWorkOrderTable.addOrderByField(fieldNum(WPPWorkOrderTable, WorkOrderId), SortOrder::Ascending);

    switch (element.args().dataset())
    {
        case tableNum(SalesTable), tableNum(SalesLine), tableNum(PurchTable), tableNum(PurchLine):
            qbdsWorkOrderTable.clearDynalinks();
            // the rest is taken are of in the init() method of the TmpIdList data source
            break;

        case tableNum(WPPWorkOrderView):
            qbdsWorkOrderTable.clearDynalinks();
            qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, WorkOrderId), element.args().record(), fieldNum(WPPWorkOrderView, WorkOrderId));
            break;

        case tableNum(WPPWorkOrderLineListPageView):
            qbdsWorkOrderTable.clearDynalinks();
            qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, WorkOrderId), element.args().record(), fieldNum(WPPWorkOrderLineListPageView, WorkOrderId));
            break;

        case tableNum(WPPWorkOrderTable):
            qbdsWorkOrderTable.clearDynalinks();
            qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, WorkOrderId), element.args().record(), fieldNum(WPPWorkOrderTable, WorkOrderId));
            break;

        case tableNum(RORRentalOrderLineView):
            qbdsOrderLine = qbdsWorkOrderTable.addDataSource(tableNum(SalesLine));
            qbdsOrderLine.relations(true);
            qbdsOrderLine.addDynalink(fieldNum(SalesLine, SalesId), element.args().record(), fieldNum(RORRentalOrderLineView, SalesId));
            break;

        case tableNum(BOBMaintenanceHistoryView):
            qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, WorkOrderId), element.args().record(), fieldNum(BOBMaintenanceHistoryView, WorkOrderId));
            break;

        case tableNum(BOBObjectView):
            qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, BOBObjectTableRecId), element.args().record(), fieldNum(BOBObjectView, RecId));
            break;

        case tableNum(WPPWorkOrderLineListPageView):
            qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, BOBObjectTableRecId), element.args().record(), fieldNum(WPPWorkOrderLineListPageView, BOBObjectTableRecId));
            break;

        case tableNum(BOBTmpObjectAvailability):
            qbdsWorkOrderTable.addDynalink(fieldNum(WPPWorkOrderTable, BOBObjectTableRecId), element.args().record(), fieldNum(BOBTmpObjectAvailability, ObjectTableRecId));
            break;

        case tableNum(ProjTable):
            qbdsWorkOrderTable.clearDynalinks();
            projTable = element.args().record();
            filterOnProjTable(projTable);
            break;

        case tableNum(ProjCostTrans):
            qbdsWorkOrderTable.clearDynalinks();
            projCostTrans = element.args().record();
            filterOnProjTable(projCostTrans.projTable());
            break;

        case tableNum(ProjEmplTrans):
            qbdsWorkOrderTable.clearDynalinks();
            projEmplTrans = element.args().record();
            filterOnProjTable(projEmplTrans.projTable());
            break;

        case tableNum(ProjItemTrans):
            qbdsWorkOrderTable.clearDynalinks();
            projItemTrans = element.args().record();
            filterOnProjTable(projItemTrans.projTable());
            break;

        case tableNum(ProjRevenueTrans):
            qbdsWorkOrderTable.clearDynalinks();
            projRevenueTrans = element.args().record();
            filterOnProjTable(projRevenueTrans.projTable());
            break;

        case tableNum(ProjInvoiceJour):
            qbdsWorkOrderTable.clearDynalinks();
            projInvoiceJour = element.args().record();
            qbdsWorkOrderTable.addRange(fieldNum(WPPWorkOrderTable, ProjInvoiceProjId)).value(SysQuery::value(projInvoiceJour.ProjInvoiceProjId));
            break;

        default:
            break;
    }

    this.cacheAddMethod(tableMethodStr(WPPWorkOrderTable, BOBdisplayMaintenanceWarning));
    this.cacheAddMethod(tableMethodStr(WPPWorkOrderTable, BSADisplayActiveContractId));
}

