public void updateDesign()
{
    boolean hasBillingRules;
    boolean canCreateInvoiceProposal;

    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, OrganizationParty)).allowEdit(!WorkOrderTable.OrderRecId);
    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, CustVendAccountType)).allowEdit(!WorkOrderTable.OrderRecId);
    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, BOBObjectTableRecId)).allowEdit(!WorkOrderTable.OrderRecId);
    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, ProjInvoiceProjId)).allowEdit(false);
    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, ProjGroupId)).allowEdit(false);
    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, RORVenueCode)).allowEdit(!WorkOrderTable.OrderRecId);
    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, LogisticsLocationRecId)).allowEdit(!WorkOrderTable.OrderRecId);
    WorkOrderTable_ds.object(fieldNum(WPPWorkOrderTable, ObjectQty)).allowEdit(WorkOrderTable.BOBObjectTableRecId
        && WorkOrderTable.BOBObjectTable().rentalType().Classification == BOBRentalTypeClassification::Bulk);

    hasBillingRules          = PSAContractLineItems::hasBillingRules(WorkOrderTable.ProjInvoiceProjId);
    canCreateInvoiceProposal = WPPWorkOrderTable::canCreateInvoiceProposal(WorkOrderTable.WorkOrderId);

    if (hasBillingRules)
    {
        psaProjProposalSelection.visible(false);
        psaProjProposalSelectionCLIN.visible(true);
    }
    else
    {
        psaProjProposalSelection.visible(true);
        psaProjProposalSelectionCLIN.visible(false);
    }

    psaProjProposalSelection.enabled(canCreateInvoiceProposal);
    psaProjProposalSelectionCLIN.enabled(canCreateInvoiceProposal);
}

public void create(boolean _append = false)
{
    WPPWorkOrderTable   newWorkOrderTable;

    if (workOrderTableForm.create())
    {
        newWorkOrderTable = workOrderTableForm.parmWorkOrderTable();

        if (newWorkOrderTable)
        {
            if (workOrderTableForm.parmShowDelayedMaintenance())
            {
                workOrderTableForm.BOBMoveDelayedMainteance();
            }

            // Handle super(_append) functionality manually. A dialog is called
            // to create the record.  Record Templates affect the behavior of
            // the create record dialog.
            // super(_append);
            WorkOrderTable.clear();
            WorkOrderTable.data(newWorkOrderTable);
            WorkOrderLine_ds.research(true);
            WorkOrderTable_ds.rereadReferenceDataSources();
            lineViewTab.setFocus();

            Args args = new Args();
            args.caller(element);
            args.record(WorkOrderTable);

            new MenuFunction(menuitemDisplayStr(WPPWorkOrderTable), MenuItemType::Display).run(args);
        }
    }
    else
    {
        if (TradeFormHelper::isCalledFromForm(element.args()))
        {
            element.close();
        }
    }
}

