public boolean validateWrite()
{
    boolean         ret;
    TransDateTime   nullDateTime = DateTimeUtil::addDays(utcDateTimeNull(), 1);

    ret = super();

    if (ret)
    {
        // check it only when work order line is manually created by a user.
        if ((WorkOrderLine.RequestedStartDateTime >= nullDateTime && WorkOrderLine.RequestedEndDateTime < nullDateTime)
        ||  (WorkOrderLine.RequestedStartDateTime < nullDateTime && WorkOrderLine.RequestedEndDateTime >= nullDateTime))
        {
            if (WorkOrderLine.RequestedStartDateTime >= nullDateTime)
            {
                ret = checkFailed(strFmt("@DNR8255",
                    fieldId2pname(tableNum(WPPWorkOrderLine), fieldNum(WPPWorkOrderLine, RequestedEndDateTime)),
                    fieldId2pname(tableNum(WPPWorkOrderLine), fieldNum(WPPWorkOrderLine, RequestedStartDateTime))));
            }
            else
            {
                ret = checkFailed(strFmt("@DNR8255",
                    fieldId2pname(tableNum(WPPWorkOrderLine), fieldNum(WPPWorkOrderLine, RequestedStartDateTime)),
                    fieldId2pname(tableNum(WPPWorkOrderLine), fieldNum(WPPWorkOrderLine, RequestedEndDateTime))));
            }
        }
    }

    return ret;
}

