public boolean validateDelete()
{
    boolean ret;

    ret = super();

    WPPWorkOrderStatusRegistration  registration;
    WPPWorkOrderStatusGroup         workOrderStatusGroup;
    WPPWorkOrderTaskTable           workOrderTaskTable;

    select firstOnly registration
    where registration.Status == workOrderLine.Status
        exists join workOrderStatusGroup
        where workOrderStatusGroup.WorkOrderStatusGroupId == registration.WorkOrderStatusGroupId
            join TableId from workOrderTaskTable
            where workOrderTaskTable.WorkOrderStatusGroupId == workOrderStatusGroup.WorkOrderStatusGroupId
            &&    workOrderTaskTable.TaskId                 == workOrderLine.TaskId;

    WPPWorkOrderTableInteractionHelper interactionHelper = WPPWorkOrderTableInteractionHelper::construct();
    interactionHelper.initLineActions(workOrderLine, registration);

    WPPFreeTxt msgTxt;

    msgTxt = interactionHelper.parmButtonProjItemReqEditEnabled()       ? msgTxt + ctrlProjItemReqEdit.labelText() + ', ' : msgTxt;
    msgTxt = interactionHelper.parmButtonSalesOrderTableEditEnabled()   ? msgTxt + ctrlSalesOrderTableEdit.labelText() + ', ' : msgTxt;
    msgTxt = interactionHelper.parmButtonPurchOrderTableEditEnabled()   ? msgTxt + ctrlPurchOrderTableEdit.labelText() + ', ' : msgTxt;
    msgTxt = interactionHelper.parmButtonProdTableEditEnabled()         ? msgTxt + ctrlProdTableEdit.labelText() + ', ' : msgTxt;
    msgTxt = interactionHelper.parmButtonPurchReqTableEditEnabled()     ? msgTxt + ctrlPurchReqTableEdit.labelText() + ', ' : msgTxt;
    msgTxt = interactionHelper.parmButtonPurchRFQCaseTableEditEnabled() ? msgTxt + ctrlPurchRFQCaseTableEdit.labelText() + ', ' : msgTxt;
    msgTxt = interactionHelper.parmButtonSalesAgreementDetEditEnabled() ? msgTxt + ctrlSalesAgreementDetailsEdit.labelText() + ', ' : msgTxt;
    msgTxt = interactionHelper.parmButtonPurchAgreementDetEditEnabled() ? msgTxt + ctrlPurchAgreementDetailsEdit.labelText() + ', ' : msgTxt;

    if (msgTxt)
    {
        msgTxt = strDel(msgTxt, strLen(msgTxt) -1 , 2);
    }

    if (ret && msgTxt)
    {
        ret = Box::yesNo(strFmt("@DNR:DeleteWorkOrderLineConfirmationMsg", msgTxt), DialogButton::No) == DialogButton::Yes;
    }

    return ret;
}

public void init()
{
    QueryBuildDataSource    qbds;
    DNRTmpIdList            tmpData;
    Common                  callerBuffer;
    WPPWorkOrderTable       workOrderTableLocal;
    WPPWorkOrderLine        workOrderLineLocal;
    FieldId                 fieldIdProjId, fieldIdHeaderId;
    ;

    switch(element.args().dataset())
    {
        case tableNum(SalesTable):
            fieldIdProjId = fieldNum(SalesTable, ProjId);
            fieldIdHeaderId = fieldNum(SalesTable, SalesId);
            break;

        case tableNum(SalesLine):
            fieldIdProjId = fieldNum(SalesLine, ProjId);
            break;

        case tableNum(PurchTable):
            fieldIdProjId = fieldNum(PurchTable, ProjId);
            fieldIdHeaderId = fieldNum(PurchTable, PurchId);
            break;

        case tableNum(PurchLine):
            fieldIdProjId = fieldNum(PurchLine, ProjId);
            break;

        default:
            // do nothing
            break;
    }

    if(fieldIdProjId) // when called from any of the above 4 tables
    {
        this.linkType(DataSourceLinkTypePropertyValues::Exist);
        this.joinSource(WorkOrderTable_ds.id());
    }

    super();

    if(fieldIdProjId)
    {
        qbds = this.queryBuildDataSource();
        qbds.relations(false);
        qbds.addLink(fieldNum(WPPWorkOrderTable, RecId), fieldNum(DNRTmpIdList, RefRecId));

        callerBuffer = element.args().record();

        WPPWorkOrderTable_TmpIdListData tmpIdListData = new WPPWorkOrderTable_TmpIdListData();
        tmpIdListData.populate(callerBuffer, fieldIdHeaderId);

        TmpIdList.linkPhysicalTableInstance(tmpIdListData.getTmpIdList());

    }
}

[SuppressBPWarningAttribute("BPCheckSkipStatementValidation", "Cannot use skip method without altering functional behavior")]
void clicked()
{
    WPPWorkOrderTable workOrderTableDel;
    ;

    workOrderTableDel = WPPWorkOrderTable::find(WorkOrderTable.WorkOrderId);

    if(workOrderTableDel
    && workOrderTableDel.validateDelete()
    && Box::yesNo(strFmt("@SYS302359", tablePName(WPPWorkOrderTable)), DialogButton::No, "@SYS24848") == DialogButton::Yes)
    {
        delete_from workOrderTableDel
        where workOrderTableDel.WorkOrderId == WorkOrderTable.WorkOrderId;

        WorkOrderTable_ds.research();
    }
}

