public class WPPWorkOrderTemplateTable extends FormRun
{
    DNRSortingFormHelper    sortingHelper;

}

public void init()
{
    super();
    //Initialize splitter
    sortingHelper =  DNRSortingFormHelper::construct(WPPWorkOrderTemplateLine_ds, fieldNum(WPPWorkOrderTemplateLine, LineNumber), ButtonElementUp, ButtonElementDown);
}

public Common resolveTemplateLineReference(FormReferenceGroupControl _formReferenceGroupControl)
{
    int                             control, lines;
    boolean                         values = false;
    FormControl                     formControl;
    FieldBinding                    binding;
    DictField                       dictField;
    Query                           query;
    QueryBuildDataSource            qbds;
    QueryRun                        queryRun;
    SysReferenceTableLookup         lookup;
    Common                          common;

    query = new Query();
    qbds = query.addDataSource(tableNum(WPPWorkOrderTemplateLine));
    qbds.addRange(fieldNum(WPPWorkOrderTemplateLine, TemplateId)).value(SysQuery::value(WPPWorkOrderTemplateLine.TemplateId));
    qbds.addRange(fieldNum(WPPWorkOrderTemplateLine, RecId)).value(SysQuery::valueNot(WPPWorkOrderTemplateLine.RecId));

    for (control = 1; control <= _formReferenceGroupControl.controlCount(); control++)
    {
        formControl = _formReferenceGroupControl.controlNum(control);

        if (formControl.valueStr())
        {
            values = true;
            binding = formControl.fieldBinding();
            dictField = new dictField(binding.tableId(), binding.fieldId());

            if (dictField.type() == Types::String)
            {
                qbds.addRange(dictField.id()).value(strFmt('(%1.%2 like "%3")', qbds.name(), dictField.id(), formControl.valueStr()));
            }
            else
            {
                qbds.addRange(dictField.id()).value(SysQuery::value(formControl.valueStr()));
            }
        }
    }

    if (values)
    {
        queryRun = new queryRun(query);
        lines = SysQuery::countTotal(queryRun);

        if (lines == 1)
        {
            queryRun.next();
            common = queryRun.get(tableNum(WPPWorkOrderTemplateLine));
        }
        else
        {
            if (!lines)
            {
                qbds.clearRange(fieldNum(WPPWorkOrderTemplateLine, LineNumber));
                qbds.clearRange(fieldNum(WPPWorkOrderTemplateLine, TaskId));
            }

            lookup = SysReferenceTableLookup::newParameters(tableNum(WPPWorkOrderTemplateLine), _formReferenceGroupControl);
            lookup.parmQuery(query);
            lookup.addLookupfield(fieldNum(WPPWorkOrderTemplateLine, LineNumber));
            lookup.addLookupfield(fieldNum(WPPWorkOrderTemplateLine, TaskId));
            common = lookup.performFormLookup();
        }
    }

    return common;
}

public int active()
{
    int ret;

    ret = super();

    this.updateDesign();

    return ret;
}

