}

public void write()
{
    super();

    if (!WPPWorkOrderDateTimeTemplate::exist(WPPWorkOrderTemplateLine.RecId))
    {
        WPPWorkOrderDateTimeTemplate_ds.initValue();
        WPPWorkOrderDateTimeTemplate_ds.write();
    }
}

