    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, RORRentalStartTimeType)).visible(WPPWorkOrderDateTimeTemplate.StartTimeSynchSource == WPPStartEndTimeSynchSource::RentalDate && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, StartTimeTemplateLineRef)).visible(WPPWorkOrderDateTimeTemplate.StartTimeSynchSource == WPPStartEndTimeSynchSource::TemplateLine && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, StartTimeFixed)).visible(WPPWorkOrderDateTimeTemplate.StartTimeSynchSource == WPPStartEndTimeSynchSource::Fixed && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, StartTimeOffsetDuration)).visible(WPPWorkOrderDateTimeTemplate.StartTimeSynchSource != WPPStartEndTimeSynchSource::Fixed && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, StartTimeOffsetUnit)).visible(WPPWorkOrderDateTimeTemplate.StartTimeSynchSource != WPPStartEndTimeSynchSource::Fixed && sync);

    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, RORRentalEndDateType)).visible(WPPWorkOrderDateTimeTemplate.EndDateSynchSource == WPPStartDateSynchSource::RentalDate && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, EndDateTemplateLineRef)).visible(WPPWorkOrderDateTimeTemplate.EndDateSynchSource == WPPStartDateSynchSource::TemplateLine && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, EndDateOffsetDuration)).visible(WPPWorkOrderDateTimeTemplate.SynchMode != WPPDateTimeSynchMode::None && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, EndDateOffsetUnit)).visible(WPPWorkOrderDateTimeTemplate.SynchMode != WPPDateTimeSynchMode::None && sync);

    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, RORRentalEndTimeType)).visible(WPPWorkOrderDateTimeTemplate.EndTimeSynchSource == WPPStartEndTimeSynchSource::RentalDate && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, EndTimeTemplateLineRef)).visible(WPPWorkOrderDateTimeTemplate.EndTimeSynchSource == WPPStartEndTimeSynchSource::TemplateLine && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, EndTimeFixed)).visible(WPPWorkOrderDateTimeTemplate.EndTimeSynchSource == WPPStartEndTimeSynchSource::Fixed && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, EndTimeOffsetDuration)).visible(WPPWorkOrderDateTimeTemplate.EndTimeSynchSource != WPPStartEndTimeSynchSource::Fixed && sync);
    WPPWorkOrderDateTimeTemplate_ds.object(fieldNum(WPPWorkOrderDateTimeTemplate, EndTimeOffsetUnit)).visible(WPPWorkOrderDateTimeTemplate.EndTimeSynchSource != WPPStartEndTimeSynchSource::Fixed && sync);

