public Common resolveReference()
{
    WPPWorkOrderTemplateLineNumber  lineNumber;
    WPPWorkOrderTemplateLine        templateLine;
    Str60   lineNumberStr = this.filterValue(AbsoluteFieldBinding::construct(fieldStr(WPPWorkOrderTemplateLine, LineNumber), tableStr(WPPWorkOrderTemplateLine))).value();

    lineNumber = str2Int(lineNumberStr);

    if(lineNumber != WPPWorkOrderTemplateLine.LineNumber)
    {
        templateLine = WPPWorkOrderTemplateLine::find(WPPWorkOrderTemplateLine.TemplateId, lineNumber);
    }

    //ret = super();

    return templateLine;
}
