public void  initFromPurchTable(PurchTable _purchTable)
{

    this.PurchId               = _purchTable.PurchId;
    this.OrderAccount          = _purchTable.OrderAccount;
    this.InvoiceAccount        = _purchTable.InvoiceAccount;

    this.DeliveryName          = _purchTable.DeliveryName ;
    this.DeliveryPostalAddress = _purchTable.DeliveryPostalAddress;

    this.PurchaseOrder         = _purchTable.PurchId;
    this.VendorRef             = _purchTable.VendorRef;
    this.DlvTerm               = _purchTable.DlvTerm;
    this.VendGroup             = _purchTable.VendGroup;
    this.Purchaser             = _purchTable.WorkerPurchPlacer;
    this.LanguageId            = _purchTable.LanguageId;
    this.Payment               = _purchTable.Payment;
    this.FixedDueDate          = _purchTable.FixedDueDate;
    this.CashDiscCode          = _purchTable.CashDisc;

    if(TaxParameters::find().CashDiscOnInvoice)
    {
        this.CashDiscPercent   = _purchTable.CashDiscPercent;
    }

    this.Qty                   = 0;
    this.Volume                = 0;
    this.Weight                = 0;
    this.CostValue             = 0;
    this.SumLineDisc           = 0;
    this.SumMarkup             = 0;
    this.PurchBalance          = 0;
    this.EndDisc               = 0;
    this.RoundOff              = 0;
    this.Amount                = 0;
    this.CurrencyCode          = _purchTable.CurrencyCode;

    this.Triangulation         = Currency::triangulation(_purchTable.CurrencyCode, this.Date);
    this.ExchRate              = ExchangeRateHelper::exchRate(_purchTable.CurrencyCode, this.Date);
    this.ExchRateSecondary     = ExchangeRateHelper::exchRateSecond(_purchTable.CurrencyCode, this.Date);
    this.DefaultDimension      = this.copyDimension(_purchTable.DefaultDimension);
}

