public boolean isSummaryUpdated()
{
    Query       query = this.queryVendRORSubStatusSalesLink();
    QueryRun    queryRun;

    query.dataSourceTable(tableNum(VendRORSubStatusPurchLink)).firstOnly(true);
    query.dataSourceTable(tableNum(VendRORSubStatusPurchLink)).addRange(fieldNum(VendRORSubStatusPurchLink, PurchId)).value(SysQuery::valueNot(queryValue(this.PurchId)));

    queryRun = new QueryRun(query);

    return queryRun.next();
}

/// <summary>
/// Retrieves a dimension set that holds the merged combination of the current <c>defaultDimension</c>
/// field on this table and the provided dimension set.
/// </summary>
/// <param name="_primaryDefaultDimension">
/// A first dimension set to merge with the current <c>defaultDimension</c> field on this table.
