public static client boolean exist(PurchId  _purchId)
{
    return _purchId && (select firstOnly RecId from VendRORSubStatusJour
                        where VendRORSubStatusJour.PurchId == _purchId).RecId;
}

public static client server VendRORSubStatusJour findFromVendRORSubStatusTrans(PurchId                   _purchId,
                                                                               RORSubStatusId_SubRental  _subStatusId,
                                                                               TransDate                 _date,
                                                                               boolean                   _update = false)
{
    VendRORSubStatusJour  subStatusJour;

    if (_subStatusId)
    {
        subStatusJour.selectForUpdate(_update);

        select firstonly subStatusJour
        where subStatusJour.PurchId       == _purchId
        &&    subStatusJour.SubStatusId   == _subStatusId
        &&    subStatusJour.Date          == _date;
    }

    return subStatusJour;
}

public static client server VendRORSubStatusJour findRecId(recId recId, boolean update = false)
{
    VendRORSubStatusJour subStatusJour;

    subStatusJour.selectForUpdate(update);

    select firstonly subStatusJour
    where subStatusJour.RecId == recId;

    return subStatusJour;
}

public static client server int numberOfConfirm(PurchTable   _source)
{
    VendRORSubStatusJour  subStatusJour;

    select count (RecId) from subStatusJour
    where subStatusJour.PurchId == _source.PurchId;

    return any2int(subStatusJour.RecId);
}

public void sendElectronically(PrintCopyOriginal _printCopyOriginal, boolean _showWarning = true)
{
    if (this.canXMLBeSent())
    {
        Args args = new Args();
        args.parmEnumType(enumnum(PrintCopyOriginal));
        args.parmEnum(_printCopyOriginal);
        args.record(this);

        new MenuFunction(menuitemOutputStr(EInvoiceGeneration), MenuItemType::Output).run(args);

        info("@SYS344889");
    }
    else
    {
        if (_showWarning)
        {
            warning (strFmt("@SYS100814", this.InvoiceAccount));
        }
    }
}
