}

/// <summary>
/// Returns the default change tracking query.
/// </summary>
/// <returns>The default change tracking query.</returns>
public static Query defaultCTQuery()
{
    Query salesLineQuery = new Query();

    var salesLineDS = salesLineQuery.addDataSource(tableNum(SalesLine));
    salesLineDS.addRange(fieldNum(SalesLine, SalesType)).value(queryValue(SalesType::RORRental));

    var ecoResCategoryDS = salesLineDS.addDataSource(tableNum(EcoResCategory));
    ecoResCategoryDS.addLink(fieldNum(SalesLine, SalesCategory), fieldNum(EcoResCategory, RecId));
    ecoResCategoryDS.joinMode(JoinMode::OuterJoin);
    ecoResCategoryDS.fetchMode(QueryFetchMode::One2One);

    var defaultDimensionDS = salesLineDS.addDataSource(tableNum(DimensionAttributeValueSet));
    defaultDimensionDS.addLink(fieldNum(SalesLine, DefaultDimension), fieldNum(DimensionAttributeValueSet, RecId));
    defaultDimensionDS.joinMode(JoinMode::OuterJoin);
    defaultDimensionDS.fetchMode(QueryFetchMode::One2One);

    var ledgerDimensionDS = salesLineDS.addDataSource(tableNum(DimensionAttributeValueCombination));
    ledgerDimensionDS.addLink(fieldNum(SalesLine, LedgerDimension), fieldNum(DimensionAttributeValueCombination, RecId));
    ledgerDimensionDS.joinMode(JoinMode::OuterJoin);
    ledgerDimensionDS.fetchMode(QueryFetchMode::One2One);

    var salesLineForeignTradeCategoryDS = salesLineDS.addDataSource(tableNum(SalesLineForeignTradeCategory));
    salesLineForeignTradeCategoryDS.addLink(fieldNum(SalesLine, DataAreaId), fieldNum(SalesLineForeignTradeCategory, SalesLineDataAreaId));
    salesLineForeignTradeCategoryDS.addLink(fieldNum(SalesLine, InventTransId), fieldNum(SalesLineForeignTradeCategory, SalesLineInventTransId));
    salesLineForeignTradeCategoryDS.joinMode(JoinMode::OuterJoin);
    salesLineForeignTradeCategoryDS.fetchMode(QueryFetchMode::One2One);

    // InventDim should not change.

    LogisticsPostalAddressBaseEntityChangeTrackingHelper::addPostalAddressBasedOnAddressRecIdLink(salesLineDS, fieldNum(SalesLine, DeliveryPostalAddress));

    var ecoResCategory_IntrastatCommodityDS = salesLineDS.addDataSource(tableNum(EcoResCategory));
    ecoResCategory_IntrastatCommodityDS.addLink(fieldNum(SalesLine, IntrastatCommodity), fieldNum(EcoResCategory, RecId));
    ecoResCategory_IntrastatCommodityDS.joinMode(JoinMode::OuterJoin);
    ecoResCategory_IntrastatCommodityDS.fetchMode(QueryFetchMode::One2One);

    var taxWithholdItemGroupHeading_THDS = salesLineDS.addDataSource(tableNum(TaxWithholdItemGroupHeading_TH));
    taxWithholdItemGroupHeading_THDS.addLink(fieldNum(SalesLine, TaxWithholdItemGroupHeading_TH), fieldNum(TaxWithholdItemGroupHeading_TH, RecId));
    taxWithholdItemGroupHeading_THDS.joinMode(JoinMode::OuterJoin);
    taxWithholdItemGroupHeading_THDS.fetchMode(QueryFetchMode::One2One);

    var creditNoteReasonTableRefDS = salesLineDS.addDataSource(tableNum(ReasonTableRef));
    creditNoteReasonTableRefDS.addLink(fieldNum(SalesLine, CreditNoteReasonCode), fieldNum(ReasonTableRef, RecId));
    creditNoteReasonTableRefDS.joinMode(JoinMode::OuterJoin);
    creditNoteReasonTableRefDS.fetchMode(QueryFetchMode::One2One);

    var salesLine_IntrastatDS = salesLineDS.addDataSource(tableNum(SalesLine_Intrastat));
    salesLine_IntrastatDS.addLink(fieldNum(SalesLine, RecId), fieldNum(SalesLine_Intrastat, SalesLine));
    salesLine_IntrastatDS.joinMode(JoinMode::OuterJoin);
    salesLine_IntrastatDS.fetchMode(QueryFetchMode::One2One);

    var salesLine_BRDS = salesLineDS.addDataSource(tableNum(SalesLine_BR));
    salesLine_BRDS.addLink(fieldNum(SalesLine, RecId), fieldNum(SalesLine_BR, SalesLine));
    salesLine_BRDS.joinMode(JoinMode::OuterJoin);
    salesLine_BRDS.fetchMode(QueryFetchMode::One2One);

    var deliveryCFOPTableDS = salesLine_BRDS.addDataSource(tableNum(CFOPTable_BR));
    deliveryCFOPTableDS.addLink(fieldNum(SalesLine_BR, DeliveryCFOPTable_BR), fieldNum(CFOPTable_BR, RecId));
    deliveryCFOPTableDS.joinMode(JoinMode::OuterJoin);
    deliveryCFOPTableDS.fetchMode(QueryFetchMode::One2One);

    var cfopTable_BRDS = salesLine_BRDS.addDataSource(tableNum(CFOPTable_BR));
    cfopTable_BRDS.addLink(fieldNum(SalesLine_BR, CFOPTable_BR), fieldNum(CFOPTable_BR, RecId));
    cfopTable_BRDS.joinMode(JoinMode::OuterJoin);
    cfopTable_BRDS.fetchMode(QueryFetchMode::One2One);

    var fiscalDocumentType_BRDS = salesLine_BRDS.addDataSource(tableNum(FiscalDocumentType_BR));
    fiscalDocumentType_BRDS.addLink(fieldNum(SalesLine_BR, FiscalDocumentType_BR), fieldNum(FiscalDocumentType_BR, RecId));
    fiscalDocumentType_BRDS.joinMode(JoinMode::OuterJoin);
    fiscalDocumentType_BRDS.fetchMode(QueryFetchMode::One2One);

    return salesLineQuery;
}

public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRRentalOrderLineEntity, SalesLine):
            SalesLine salesLine = _dataSourceCtx.getBuffer();
            salesLine.SalesType = this.DNRSalesType;
            _dataSourceCtx.setBuffer(salesLine);
            SalesTable salesTable = SalesTable::find(this.SalesOrderNumber);



            break;
    }

    super(_entityCtx, _dataSourceCtx);
}

