public Common findEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    Common ret;

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, AccountingDistributionTemplate):
            ret = this.findAccountingDistributionTemplate();
            break;

        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, DeliveryLogisticsPostalAddress):
            ret = TradeEntitySpecificDeliveryAddress::findLogisticsPostalAddressBaseEntityDataSource();
            break;

        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, InvoiceLogisticsPostalAddress):
            ret = this.findInvoiceAddressForPurchaseOrderHeader();
            break;

        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, PurchTable_W):
            ret = PurchTable_W::findByPurchTable(this.RecId, true);
            break;

        default:
            ret = super(_entityCtx, _dataSourceCtx);
    }

    return ret;
}

public void mapEntityToDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, PurchTable):
            PurchTable purchTable = _dataSourceCtx.getBuffer();

            if (this.isFieldSet(fieldNum(DNRSubRentalOrderHeaderEntity, AccountingDistributionTemplateName)))
            {
                purchTable.AccountingDistributionTemplate = this.findAccountingDistributionTemplate().RecId;
            }

            if (this.scopeIdentifier())
            {
                // Skip creation of markup when doing data migration
                purchTable.SkipCreateMarkup = NoYes::Yes;
            }

            break;

        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, DeliveryLogisticsPostalAddress):
            if (_dataSourceCtx.isAnyEntityFieldSet(this))
            {
                this.TradeEntitySpecificDeliveryAddress::mapLogisticsPostalAddressBaseEntityDataSourceToEntity(_dataSourceCtx);
            }

            break;
    }

    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, PurchTable):
            PurchTable purchTable = _dataSourceCtx.getBuffer();
            purchTable.fieldState(fieldnum(PurchTable, PurchStatus), FieldState::NotSet);
            break;

        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, ReasonTableRef):
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, TransportationDocument):
            if (!_dataSourceCtx.isAnyFieldSetToNonDefaultValue(tableNum(DNRSubRentalOrderHeaderEntity)))
            {
                _dataSourceCtx.setAllFieldStatesToNotSet();
            }
            break;
    }
}

public void mapDataSourceToEntity(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    if (_dataSourceCtx.name() == dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, PurchTable))
    {
        PurchTable purchTable = _dataSourceCtx.getBuffer();

        this.TradeEntitySpecificDeliveryAddress::mapSavedPostalAddressToEntity(
            _entityCtx,
            purchTable.DeliveryPostalAddress,
            dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, DeliveryLogisticsPostalAddress));

        if (this.DefaultLedgerDimension != purchTable.DefaultDimension)
        {
            DimensionSetEntity dimensionSet;

            select firstonly forupdate dimensionSet where
                dimensionSet.RecId == purchTable.DefaultDimension;

            this.DefaultLedgerDimensionDisplayValue = dimensionSet.DisplayValue;

            DictDataEntity dictEntity = new DictDataEntity(tableNum(DNRSubRentalOrderHeaderEntity));
            int dataSourceNumber = dictEntity.fieldDatasource(fieldNum(DNRSubRentalOrderHeaderEntity, DefaultLedgerDimensionDisplayValue));

            this.fromViewDataSource(dataSourceNumber, dimensionSet);
        }
    }

    super(_entityCtx, _dataSourceCtx);
}

public void initializeEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, PurchTable):
            // clear out 'isFieldSet' so the defaulting logic does not consider fields that were set by initValue as specified by the user
            _dataSourceCtx.setAllFieldStatesToNotSet();
            break;
    }
}

public boolean insertEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, PurchTable_W):
            // PurchTable_W was created during PurchTable.insert(), so need to resolve the conflict:
            _entityCtx.detectConflictForDatasource(_dataSourceCtx);
            break;

        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, TMSPurchTable):
            // TMSPurchTable might have been created during the insert or update of PurchTable
            _entityCtx.detectConflictForDatasource(_dataSourceCtx);
            break;
    }

    boolean ret = super(_entityCtx, _dataSourceCtx);

    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, DeliveryLogisticsPostalAddress):
            this.TradeEntitySpecificDeliveryAddress::postInsertLogisticsPostalAddressBaseEntityDataSource(_dataSourceCtx);
            break;
    }

    return ret;
}

public boolean updateEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, PurchTable):
            if (this.CurrencyCode != this.orig().CurrencyCode)
            {
                PurchTable purchTable = _dataSourceCtx.getBuffer();
                purchTable.convertCurrencyCode(this.CurrencyCode, TradeCurencyConversion::TradeAgreements, purchTable.exchangeRateDateForCurrencyChange());
            }

            break;

        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, TMSPurchTable):
            _entityCtx.detectConflictForDatasource(_dataSourceCtx);
            break;
    }

    return super(_entityCtx, _dataSourceCtx);
}

public boolean deleteEntityDataSource(DataEntityRuntimeContext _entityCtx, DataEntityDataSourceRuntimeContext _dataSourceCtx)
{
    switch (_dataSourceCtx.name())
    {
        case dataEntityDataSourceStr(DNRSubRentalOrderHeaderEntity, DeliveryLogisticsPostalAddress):
            _dataSourceCtx.setDatabaseOperation(DataEntityDatabaseOperation::None);
            break;
    }

    return super(_entityCtx, _dataSourceCtx);
}

public void initValue()
{
    if (!this.skipNumberSequenceCheck())
    {
        NumberSeqRecordFieldHandler::enableNumberSequenceControlForField(
            this, fieldNum(DNRSubRentalOrderHeaderEntity, PurchaseOrderNumber), PurchParameters::numRefPurchId());
    }

    super();
}

private LogisticsPostalAddress findInvoiceAddressForPurchaseOrderHeader()
{
    LogisticsPostalAddress invoiceAddress;

    select firstonly invoiceAddress
        where invoiceAddress.CountryRegionId == this.InvoiceAddressCountryRegionId
            && invoiceAddress.Street == this.InvoiceAddressStreet
            && invoiceAddress.State == this.InvoiceAddressState
            && invoiceAddress.County == this.InvoiceAddressCounty
            && invoiceAddress.City == this.InvoiceAddressCity
            && invoiceAddress.ZipCode == this.InvoiceAddressZipCode;

    return invoiceAddress;
}

private AccountingDistributionTemplate findAccountingDistributionTemplate()
{
    RecId legalEntityRecId = CompanyInfo::findDataArea(this.DataAreaId).RecId;
    return AccountingDistributionTemplate::findByNameAndLegalEntity(this.AccountingDistributionTemplateName, legalEntityRecId);
}

// This method implements the interface defined by <c>TradeEntitySpecificDeliveryAddress</c>
public RecId deliveryAddressPartyRecId()
{
    return CompanyInfo::find().RecId;
}

private static str isDeliveryAddressOrderSpecificComputedColumnDefinition()
{
    // The <c>IsDeliveryAddressOrderSpecific</c> field is only designed for import scenarios
    // so we will always return 'No' for export scenarios to save performance.
    return SysComputedColumn::returnLiteral(NoYes::No);
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (this && !PurchTable::find(this.PurchaseOrderNumber).checkUpdate(true, true, true))
    {
        return false;
    }

    return ret;
}

public static Query defaultCTQuery()
{
    Query purchaseOrderHeaderQuery = new Query();

    var purchTableDS = purchaseOrderHeaderQuery.addDataSource(tableNum(PurchTable));

    var accDistributionTemplDS = purchTableDS.addDataSource(tableNum(AccountingDistributionTemplate));
    accDistributionTemplDS.joinMode(JoinMode::OuterJoin);
    accDistributionTemplDS.addLink(fieldNum(PurchTable, AccountingDistributionTemplate), fieldNum(AccountingDistributionTemplate, RecId));

    var confirmingPODS = purchTableDS.addDataSource(tableNum(ConfirmingPO));
    confirmingPODS.joinMode(JoinMode::OuterJoin);
    confirmingPODS.relations(true);

    var vendInvoiceDeclISDS = purchTableDS.addDataSource(tableNum(VendInvoiceDeclaration_IS));
    vendInvoiceDeclISDS.joinMode(JoinMode::OuterJoin);
    vendInvoiceDeclISDS.relations(true);

    var dimAttributeValueSet = purchTableDS.addDataSource(tableNum(DimensionAttributeValueSet));
    dimAttributeValueSet.joinMode(JoinMode::OuterJoin);
    dimAttributeValueSet.addLink(fieldNum(PurchTable, DefaultDimension), fieldNum(DimensionAttributeValueSet, RecId));

    var reasonTableDS = purchTableDS.addDataSource(tableNum(ReasonTableRef));
    reasonTableDS.joinMode(JoinMode::OuterJoin);
    reasonTableDS.addLink(fieldNum(PurchTable, ReasonTableRef), fieldNum(ReasonTableRef, RecId));

    LogisticsPostalAddressBaseEntityChangeTrackingHelper::addPostalAddressBasedOnAddressRecIdLink(purchTableDS, fieldNum(PurchTable, DeliveryPostalAddress));

    var purchTableWDS = purchTableDS.addDataSource(tableNum(PurchTable_W));
    purchTableWDS.joinMode(JoinMode::OuterJoin);
    purchTableWDS.addLink(fieldNum(PurchTable, RecId), fieldNum(PurchTable_W, PurchTable));

    var logisticsPostalAddressDS = purchTableWDS.addDataSource(tableNum(LogisticsPostalAddress));
    logisticsPostalAddressDS.joinMode(JoinMode::OuterJoin);
    logisticsPostalAddressDS.addLink(fieldNum(PurchTable_W, InvoicePostalAddress_TH), fieldNum(LogisticsPostalAddress, RecId));

    var tmsPurchTableDS = purchTableDS.addDataSource(tableNum(TMSPurchTable));
    tmsPurchTableDS.joinMode(JoinMode::OuterJoin);
    tmsPurchTableDS.addLink(fieldNum(PurchTable, PurchId), fieldNum(TMSPurchTable, PurchId));

    var workerDS = purchTableDS.addDataSource(tableNum(HcmWorker));
    workerDS.joinMode(JoinMode::OuterJoin);
    workerDS.addLink(fieldNum(PurchTable, WorkerPurchPlacer), fieldNum(HcmWorker, RecId));

    workerDS = purchTableDS.addDataSource(tableNum(HcmWorker));
    workerDS.joinMode(JoinMode::OuterJoin);
    workerDS.addLink(fieldNum(PurchTable, Requester), fieldNum(HcmWorker, RecId));

    var transportationDocDS = purchTableDS.addDataSource(tableNum(TransportationDocument));
    transportationDocDS.joinMode(JoinMode::OuterJoin);
    transportationDocDS.addLink(fieldNum(PurchTable, TransportationDocument), fieldNum(TransportationDocument, RecId));

    return purchaseOrderHeaderQuery;
}
