/// <summary>
/// Form initialization
/// </summary>
public void init()
{
    if (this.args().dataset() == tableNum(DNRRFPTmpRentalTypeAvailability))
    {
        DNRRFPTmpRentalTypeAvailability tmpRentalTypeAvailability = this.args().record();

        if (tmpRentalTypeAvailability.RentalTypeId)
        {
            ttsbegin;
            BOBObjectAvailabilitySettings settingsLocal = BOBObjectAvailabilitySettings::findOrCreate(curUserId(), true, true);
            settingsLocal.RentalTypeId = tmpRentalTypeAvailability.RentalTypeId;

            if (this.args().parmObject() && (this.args().parmObject() is DNRRFPAssetAvailabilityData))
            {
                DNRRFPAssetAvailabilityData businessObjectData = this.args().parmObject();
                settingsLocal.StartDateTime = businessObjectData.parmCalculationDateTime();
            }

            settingsLocal.update();
            ttscommit;
        }
    }

    next init();
}
