protected boolean initExpectedValues(VendInvoiceInfoTable _parmTable)
{
    boolean ret = next initExpectedValues(_parmTable);

    if (_parmTable.purchTable().PurchaseType == PurchaseType::RORSubRental)
    {
        this.TotalPriceTolerancePercentage = VendParameters::find().FINSubRentalTolerancePct;
    }

    return ret;
}
