[SuppressBPWarningAttribute("BPCheckSkipStatementValidation", "Cannot use skip method without altering functional behavior"),
 SuppressBPWarningAttribute("BPCheckNestedLoopInCode", "Cannot be refactored without altering functionality of method")]
public void printInvoice(PrintCopyOriginal _printCopyOriginal, boolean _sendElectronically)
{
    FINCustInvoiceJourProforma  custInvoiceJourProforma;
    FINCustInvoiceTransProForma custInvoiceTransProforma;
    CustInvoiceJour             custInviceJourLocal;
    CustInvoiceTrans            custInvoiceTransLocal;
    CustInvoiceJour             delCustInvoiceJour;
    CustInvoiceTrans            delCustInvoiceTrans;
    Set                         markupDelSet = new Set(Types::Int64);
    MarkupTrans                 markUpTransProposal, markupTransPrint, markupDelete;
    // <GTH>
    boolean isUnrealizedVATEnabled = TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled();
    // </GTH>

    Set postedInvoiceRecIds = new Set(Types::Int64);

    for (custInvoiceJourProforma = getFirstSelection(custInvoiceJour_ds); custInvoiceJourProforma; custInvoiceJourProforma = custInvoiceJour_ds.getNext())
    {
        if (custInvoiceJourProforma.isInvoicePosted())
        {
            postedInvoiceRecIds.add(custInvoiceJourProforma.RecId);
            checkFailed(strFmt("@DNR:Placeholder12",
                               strFmt("@DNR:FailedToProcessRentalInvoiceProposalX", custInvoiceJourProforma.InvoiceId),
                               "@DNR:InvoiceProposalAlreadyPosted"));
            continue;
        }

        ttsbegin;

        custInviceJourLocal.clear();
        custInviceJourLocal = FINCustInvoiceJourProforma::copyFromToProforma(custInvoiceJourProforma, custInviceJourLocal);
        custInviceJourLocal.FINIsRentalProposal = NoYes::Yes;
        custInviceJourLocal.insert();

        FINCustInvoiceProformaSalesLink custInvoiceProformaSalesLink;
        RecordInsertList custInvoiceSalesLinkInsertList = new RecordInsertList(tableNum(CustInvoiceSalesLink), true, true, true, true, true);

        while select custInvoiceProformaSalesLink
        where custInvoiceProformaSalesLink.SalesId == custInvoiceJourProforma.SalesId
        &&    custInvoiceProformaSalesLink.InvoiceId == custInvoiceJourProforma.InvoiceId
        &&    custInvoiceProformaSalesLink.InvoiceDate == custInvoiceJourProforma.InvoiceDate
        {
            CustInvoiceSalesLink custInvoiceSalesLink;
            custInvoiceSalesLink = FINCustInvoiceProformaSalesLink::copyFromToProforma(custInvoiceProformaSalesLink, custInvoiceSalesLink);
            custInvoiceSalesLink.InvoiceId = custInviceJourLocal.InvoiceId;

            custInvoiceSalesLinkInsertList.add(custInvoiceSalesLink);
        }

        custInvoiceSalesLinkInsertList.insertDatabase();

        while select custInvoiceTransProforma
        where custInvoiceTransProforma.SalesId == custInvoiceJourProforma.SalesId
        &&    custInvoiceTransProforma.InvoiceId == custInvoiceJourProforma.InvoiceId
        &&    custInvoiceTransProforma.InvoiceDate == custInvoiceJourProforma.InvoiceDate
        {
            custInvoiceTransLocal.clear();
            custInvoiceTransLocal = FINCustInvoiceJourProforma::copyFromToProforma(custInvoiceTransProforma, custInvoiceTransLocal);
            custInvoiceTransLocal.insert();

            while select markUpTransProposal
                where markUpTransProposal.TransRecId == custInvoiceTransProforma.RecId
                    && markUpTransProposal.TransTableId == custInvoiceTransProforma.TableId
            {
                markupTransPrint = Markup::copySingleMarkupTrans(NoYes::No, markUpTransProposal, markUpTransProposal.CurrencyCode, custInvoiceTransProforma, custInvoiceTransLocal);

                markupTransPrint.CalculatedAmount = Markup::calcMarkupAmount(markupTransPrint, custInvoiceTransLocal.Qty, custInvoiceTransLocal.LineAmount, 0, false, NoYes::No, custInvoiceTransLocal.CurrencyCode, custInvoiceTransProforma.InvoiceDate);
                markupTransPrint.update();

                markupDelSet.add(markupTransPrint.RecId);
            }
        }

        Args args = new Args();
        args.record(custInviceJourLocal);

        if (_sendElectronically)
        {
            custInviceJourLocal.sendElectronically(_printCopyOriginal);
        }
        else
        {
            switch (_printCopyOriginal)
            {
                case PrintCopyOriginal::Copy:
                    new MenuFunction(menuitemOutputStr(FINSalesInvoiceCopyProforma), MenuItemType::Output).run(args);
                    break;
                case PrintCopyOriginal::Original:
                    new MenuFunction(menuitemOutputStr(FINSalesInvoiceOriginalProforma), MenuItemType::Output).run(args);
                    break;
                case PrintCopyOriginal::OriginalPrint:
                    new MenuFunction(menuitemOutputStr(FINSalesInvoiceProforma), MenuItemType::Output).run(args);
                    break;
            }
        }

        CustInvoiceSalesLink custInvoiceSalesLink;
        delete_from custInvoiceSalesLink
        where custInvoiceSalesLink.SalesId == custInviceJourLocal.SalesId
        &&    custInvoiceSalesLink.InvoiceId == custInviceJourLocal.InvoiceId
        &&    custInvoiceSalesLink.InvoiceDate == custInviceJourLocal.InvoiceDate;

        delete_from delCustInvoiceTrans
        where delCustInvoiceTrans.SalesId   == custInviceJourLocal.SalesId
        &&   delCustInvoiceTrans.InvoiceId  ==  custInviceJourLocal.InvoiceId
        &&  delCustInvoiceTrans.InvoiceDate == custInviceJourLocal.InvoiceDate
        &&  delCustInvoiceTrans.numberSequenceGroup == custInviceJourLocal.numberSequenceGroup;

        custInviceJourLocal.delete();

        SetEnumerator enumerator = markupDelSet.getEnumerator();

        while (enumerator.moveNext())
        {
            markupDelete = MarkupTrans::findRecId(enumerator.current(), true);
            if (markupDelete.RecId)
            {
                markupDelete.delete();
            }
        }

        ttscommit;
    }
}

public int active()
{
    int ret;
    RefRecId localAccountingEventRec;

    ret = super();

    localAccountingEventRec = custInvoiceJour.custTrans().AccountingEvent;

    if (localAccountingEventRec)
    {
        localAccountingEventRec = custInvoiceJour.ReversedRecId == 0;
    }

    this.enableApprovalButtons();

    return ret;
}

protected void enableApprovalButtons()
{
    FINCustInvoiceJourProformaStatusHelper statusHelper = FINCustInvoiceJourProformaStatusHelper::construct();

    MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(CustInvoiceJour_ds);

    FINCustInvoiceJourProForma custInvoiceJourProforma = multiSelectionHelper.getFirst();
    while (custInvoiceJourProforma.RecId != 0)
    {
        statusHelper.prosessStatus(custInvoiceJourProforma.ApprovalStatus);

        custInvoiceJourProforma = multiSelectionHelper.getNext();
    }

    postButton.enabled(statusHelper.canPost());
    FINCustInvoiceJourProformaConfirm.enabled(statusHelper.canConfirm());
    FINCustInvoiceJourProformaSend.enabled(statushelper.canSend());
    FINCustInvoiceJourProformaRevise.enabled(statusHelper.canRevise());
    ApproveVersionButton.enabled(statusHelper.canApprove());
    FINManualPriceComponentProforma.enabled(statusHelper.canSetPendingApproval());
    FINSalesInvoiceProformaJournalCancel.enabled(statusHelper.canCancel());
}

public void init()
{
    super();

    if (element.args().dataset() == tableNum(SalesTable))
    {
        this.query().dataSourceTable(tableNum(FINCustInvoiceJourProforma)).addRange(fieldNum(FINCustInvoiceJourProforma, RefNum)).value(queryValue(RefNum::SalesOrder));
        this.query().dataSourceTable(tableNum(FINCustInvoiceJourProforma)).clearDynalinks();

        QueryBuildDataSource queryDataSourceLink = this.query().dataSourceTable(tableNum(FINCustInvoiceJourProforma)).addDataSource(tableNum(FINCustInvoiceProFormaSalesLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldNum(FINCustInvoiceProFormaSalesLink, OrigSalesId),
                                        element.args().record(),
                                        fieldNum(SalesTable, SalesId));
    }

    QueryBuildDataSource invoiceProposalQBDS;

    invoiceProposalQBDS = this.query().dataSourceTable(tableNum(FINCustInvoiceJourProForma));
    rangeIsProFormaInvoiceActive = invoiceProposalQBDS.addRange(fieldNum(FINCustInvoiceJourProForma, IsProFormaInvoiceActive));
    rangeIsProFormaInvoiceActive.value(SysQuery::value(NoYes::Yes));

    invoiceQBDS = invoiceProposalQBDS.addDataSource(tableNum(CustInvoiceJour));
    invoiceQBDS.addLink(fieldNum(FINCustInvoiceJourProForma, InvoiceId), fieldNum(CustInvoiceJour, InvoiceId));
    invoiceQBDS.joinMode(JoinMode::NoExistsJoin);
}

display TitleFields displayInvoiceIdAndCustomerName()
{
    return strfmt("%1 : %2", custInvoiceJour.InvoiceId, CustTable::find(custInvoiceJour.InvoiceAccount).name());
}

public void executeQuery()
{
    this.executeQueryFilter();

    super();            

    element.calculateTotalProposal();
}

private void executeQueryFilter()
{
    if (rangeIsProFormaInvoiceActive)
    {
        if (showPosted)
        {
            invoiceQBDS.joinMode(JoinMode::OuterJoin);
        }
        else
        {
            invoiceQBDS.joinMode(JoinMode::NoExistsJoin);
        }
    }
}

public void jumpRef()
{
    if (custInvoiceJour.isInvoicePosted())
    {
        CustInvoiceJour custInvoiceJourPosted;

        select firstonly RecId from custInvoiceJourPosted
        where  custInvoiceJourPosted.InvoiceAccount == custInvoiceJour.InvoiceAccount
        &&     custInvoiceJourPosted.InvoiceId == custInvoiceJour.InvoiceId
        &&     custInvoiceJourPosted.LedgerVoucher == custInvoiceJour.LedgerVoucher;

        Args args = new Args();
        args.caller(element);
        args.record(custInvoiceJourPosted);
        new MenuFunction(menuitemDisplayStr(CustInvoiceJournal), MenuItemType::Display).run(args);
    }
    else
    {
        Args args = new Args();
        args.caller(element);
        args.record(custInvoiceJour);
        new MenuFunction(menuitemDisplayStr(FINCustInvoiceJournalProformaDetails), MenuItemType::Display).run(args);
    }
}

public void jumpRef()
{
    FINCustInvoiceJourProForma custInvoiceJourProforma = CustInvoiceJour_ds.cursor();
    Args args = new Args();
    args.caller(element);
    args.record(SalesTable::find(custInvoiceJourProforma.SalesId));
    MenuFunction menuFunction =new MenuFunction(menuitemDisplayStr(RORSalesTableMainTable), MenuItemType::Display);
    if (menuFunction)
    {
        menuFunction.run(args);
    }
}

public void linkActive()
{
    QueryBuildRange qbrReversedRecId;

    qbrReversedRecId = this.query().dataSourceTable(tableNum(FINCustInvoiceTransProforma)).findRange(fieldNum(FINCustInvoiceTransProforma, ReversedRecId));

    if (!qbrReversedRecId)
    {
        qbrReversedRecId = this.query().dataSourceTable(tableNum(FINCustInvoiceTransProforma)).addRange(fieldNum(FINCustInvoiceTransProforma, ReversedRecId));
    }

    if (custInvoiceJour.ReversedRecId == 0)
    {
        qbrReversedRecId.value(queryValue('0'));
    }
    else
    {
        qbrReversedRecId.value(queryValue('!0'));
    }

    super();
}

