/// <summary>
///    returns PackingSlipId of the <c>CustPackingSlipTrans</c> table.
/// </summary>
/// <param name="_custInvoiceTrans">
///    The <c>CustInvoiceTrans</c> buffer.
/// </param>
/// <returns>
/// PackingSlipId of the <c>CustPackingSlipTrans</c> table.
/// </returns>
public display PackingSlipId packingSlipId(FINCustInvoiceTransProforma _custInvoiceTrans)
{
    int pos;

    pos = conFind(conPackingSlipIdDate, _custInvoiceTrans.RecId);
    return conPeek(conPackingSlipIdDate, pos + 1);
}

/// <summary>
///    returns delivery date of the <c>CustPackingSlipTrans</c> table.
/// </summary>
/// <param name="_custInvoiceTrans">
///    The <c>CustInvoiceTrans</c> buffer.
/// </param>
/// <returns>
/// Delivery date of the <c>CustPackingSlipTrans</c> table.
/// </returns>
public display SalesShippingDate deliveryDate(FINCustInvoiceTransProforma _custInvoiceTrans)
{
    int pos;

    pos = conFind(conPackingSlipIdDate, _custInvoiceTrans.RecId);
    return conPeek(conPackingSlipIdDate, pos + 2);
}

