public void clicked()
{
    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(custInvoiceJour_ds);
    Set custInvoiceJourProformaApprovedRecIdSet = new Set(Types::Int64);

    FINCustInvoiceJourProforma custInvoiceJourProforma = helper.getFirst();

    while (custInvoiceJourProforma.RecId != 0)
    {
        if (custInvoiceJourProforma.validateMultiSelectPosting(FINCustInvoiceJournalProformaAction::Approve))
        {
            custInvoiceJourProforma.approveCurrentVersion();
            custInvoiceJourProformaApprovedRecIdSet.add(custInvoiceJourProforma.RecId);
        }

        custInvoiceJourProforma = helper.getNext();
    }

    if (!custInvoiceJourProformaApprovedRecIdSet.empty())
    {
        FINCustInvoiceJourProformaStatusManager custInvoiceJourProformaManager = FINCustInvoiceJourProformaStatusManager::construct(FINCustInvoiceJourProformaStatus::Approved);
        custInvoiceJourProformaManager.parmCustInvoiceJourProformaRecIdSet(custInvoiceJourProformaApprovedRecIdSet);
        custInvoiceJourProformaManager.run();
    }

    super();

    custInvoiceJour_ds.research(true);
}

public void clicked()
{
    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(custInvoiceJour_ds);

    FINCustInvoiceJourProforma custInvoiceJourProforma = helper.getFirst();

    while (custInvoiceJourProforma.RecId != 0)
    {
        if (custInvoiceJourProforma.validateMultiSelectPosting(FINCustInvoiceJournalProformaAction::Cancel))
        {
            Args args = new Args();
            args.caller(element);
            args.record(custInvoiceJourProforma);
            this.menufunction().run(args);
        }

        custInvoiceJourProforma = helper.getNext();
    }

    custInvoiceJour_ds.research();
}

