[Form]
public class FINCustInvoiceTransPeriods extends FormRun
{
    boolean                                     isProForma;
    FINRentalPeriodJournalizer_CustInvoiceJour  journalizer;
    SalesLine                                   salesLine;
    CustInvoiceTrans                            custInvoiceTrans;
    FINInvoiceCalculatorParm                    invoiceCalculatorParm;

}

public void init()   
{
    if (!element.args().record())
    {
        throw error("@SYS22539");
    }

    if (element.args().dataset() == tableNum(SalesParmLine))
    {
        SalesParmLine               salesParmLine = element.args().record();
        CustInvoiceJour             custInvoiceJour; 

        salesLine = salesParmLine.salesLine();

        isProForma = true;

        select firstonly invoiceCalculatorParm
        where invoiceCalculatorParm.ParmLineRecId == salesParmLine.RecId
        &&  invoiceCalculatorParm.ParmLineTableId == salesParmLine.TableId
        &&  invoiceCalculatorParm.ParmId == salesParmLine.ParmId;

        journalizer = FINRentalPeriodJournalizer::construct(custInvoiceJour, invoiceCalculatorParm) as FINRentalPeriodJournalizer_CustInvoiceJour;
        journalizer.journalize();
    }
    else if (element.args().dataset() == tableNum(CustInvoiceTrans))
    {
        custInvoiceTrans = element.args().record();
        salesLine = custInvoiceTrans.salesLine();
    }
    else if (element.args().dataset() == tableNum(FINInvoiceCalculatorParm))
    {
        CustInvoiceJour custInvoiceJour;

        invoiceCalculatorParm = element.args().record();

        select firstonly salesLine
        where salesLine.RecId   == invoiceCalculatorParm.OrderLineRecId
           && salesLine.TableId == invoiceCalculatorParm.OrderLineTableId;

        isProForma = true;

        journalizer = FINRentalPeriodJournalizer::construct(custInvoiceJour, invoiceCalculatorParm) as FINRentalPeriodJournalizer_CustInvoiceJour;
        journalizer.journalize();
    }

    super();
}

[SysClientCacheDataMethod]
public display SalesId displaySalesId()
{
    return salesLine.SalesId;
}

[SysClientCacheDataMethod]
public display LineNum displayLineNum()
{
    return salesLine.LineNum;
}

[SysClientCacheDataMethod]
public display ItemId displayItemId()
{
    return salesLine.ItemId;
}

[SysClientCacheDataMethod]
public display BOBObjectId displayObjectId()
{
    return InventDim::find(salesLine.InventDimId).BOBDisplayObjectId();
}

[SysClientCacheDataMethod]
public display CurrencyCode displayCurrencyCode()
{
    return salesLine.CurrencyCode;
}

[SysClientCacheDataMethod]
public display InvoiceId displayInvoiceId()
{
    return custInvoiceTrans.InvoiceId;
}

