Method.Source:
[Form]
public class FINProjInvoiceItemPeriods extends FormRun
{
    SalesLine                                   salesLine;
    ProjInvoiceItem                             projInvoiceItem;

}

public void init()  
{
    if (!element.args().record())
    {
        throw error("@SYS22539");
    }

    projInvoiceItem = element.args().record();
    salesLine = projInvoiceItem.salesLine();

    super();
}

[SysClientCacheDataMethod]
public display SalesId displaySalesId()
{
    return salesLine.SalesId;
}

[SysClientCacheDataMethod]
public display LineNum displayLineNum()
{
    return salesLine.LineNum;
}

[SysClientCacheDataMethod]
public display ItemId displayItemId()
{
    return salesLine.ItemId;
}

[SysClientCacheDataMethod]
public display CurrencyCode displayCurrencyCode()
{
    return salesLine.CurrencyCode;
}

[SysClientCacheDataMethod]
public display ProjInvoiceId displayInvoiceId()
{
    return projInvoiceItem.ProjInvoiceId;
}

public void init()
{
    super();

    QueryBuildDataSource projInvoiceItemCreditDS = this.queryBuildDataSource();
    projInvoiceItemCreditDS.clearLinks();
    projInvoiceItemCreditDS.addLink(fieldNum(FINProjInvoiceItemCredit, ProjInvoiceItemCreditedRecId), fieldNum(ProjInvoiceItem, RecId));
}

public void init()
{
    super();

    QueryBuildDataSource projEmplTransDS = this.queryBuildDataSource();
    projEmplTransDS.clearLinks();
    projEmplTransDS.addLink(fieldNum(FINProjInvoiceItemProjHour, ProjEmplTransRecId), fieldNum(ProjEmplTrans, RecId));
}
