[Form]
public class FINRentalCreditCreate extends FormRun
{    
    FINCreditForm           creditFormClass;
    FINCreditManager        creditManager;
    QueryBuildRange         onlyOrder;
    QueryBuildRange         rangeOrderId;
    QueryBuildRange         rangeInvoiceAccount;
    QueryBuildRange         rangeInvoiceId;
    Common                  actionBuffer;
    Common                  callerBuffer;

}

[SuppressBPWarningAttribute("BPCheckInvalidInitFormMethodWarning", "Validation of caller must be performed before invoking super()")]
public void init()
{
    creditFormClass = FINCreditForm::construct(SalesPurch::Sales);
    if (element.args().record())
    {
        callerBuffer = element.args().record();
        creditFormClass.initCallerBuffer(callerBuffer);
    }
    creditManager = FINCreditManager::construct(creditFormClass.salesPurch());

    super();

    ShowAllFilter.value(NoYes::Yes);
    CreditStatusFilter.enabled(NoYes::No);

    if (!callerBuffer)
    {
        FINCreditAddDropDialog.visible(false);
        FINCreditEditDropDialog.visible(false);
        ApproveButton.visible(true);
        RejectButton.visible(true);
    }

    creditFormClass.parmCreditFormRun(element);
}

public void executeQuery()
{
    if (callerBuffer)
    {
        if (creditFormClass.orderId())
        {
            rangeOrderId.value(queryValue(creditFormClass.orderId()));
        }

        if (creditFormClass.invoiceAccount())
        {
            rangeInvoiceAccount.value(queryValue(creditFormClass.invoiceAccount()));
        }
        // Add range to show only 'active'/not credited invoices
        if (creditFormClass.invoiceId())
        {
            rangeInvoiceId.value(queryValue(creditFormClass.invoiceId()));
        }

        onlyOrder.value(enum2Value(RefNum::SalesOrder));
    }

    super();
}

void init()
{
    super();

    if (callerBuffer)
    {
        QueryBuildDataSource qbdsCustInvoiceJour = this.queryBuildDataSource();
        qbdsCustInvoiceJour.clearDynalinks();

        rangeInvoiceAccount = qbdsCustInvoiceJour.addRange(fieldNum(CustInvoiceJour, InvoiceAccount));
        rangeInvoiceId      = qbdsCustInvoiceJour.addRange(fieldNum(CustInvoiceJour, InvoiceId));
        rangeOrderId        = qbdsCustInvoiceJour.addRange(fieldNum(CustInvoiceJour, SalesId));
        onlyOrder           = qbdsCustInvoiceJour.addRange(fieldNum(CustInvoiceJour, RefNum));

        creditFormClass.extendQuery(qbdsCustInvoiceJour);
    }
    else
    {
        QueryBuildDataSource qbdsCustInvoiceJour = this.queryBuildDataSource();
        QueryBuildDataSource qbdsCreditLine = qbdsCustInvoiceJour.addDataSource(tableNum(FINCreditLine));
        qbdsCreditLine.relations(true);
        qbdsCreditLine.joinMode(JoinMode::ExistsJoin);
    }
}

public int active()
{
    int ret = super();

    ApproveButton.enabled(!FINCreditLine::canCreditJourBeApproved(CustInvoiceJour.TableId, CustInvoiceJour.RecId));
    RejectButton.enabled(!FINCreditLine::canCreditJourBeRejected(CustInvoiceJour.TableId, CustInvoiceJour.RecId));
    buttonUpdateInvoice.enabled(FINCreditLine::canCreditJourBePosted(CustInvoiceJour.TableId, CustInvoiceJour.RecId));
    buttonProformaInvoice.enabled(FINCreditLine::canCreditJourBeProformaPosted(CustInvoiceJour.TableId, CustInvoiceJour.RecId));

    return ret;
}

public void jumpRef()
{
    //super();
    Args args = new Args();
    args.caller(element);
    args.record(SalesTable::find(CustInvoiceJour.SalesId));
    MenuFunction menuFunction =new MenuFunction(menuitemDisplayStr(RORSalesTableMainTable), MenuItemType::Display);
    if (menuFunction)
    {
        menuFunction.run(args);
    }
}

public void init()
{
    super();

    QueryBuildDataSource qbdsFINCreditLine = this.queryBuildDataSource();

    if (callerBuffer)
    {
        qbdsFINCreditLine.addRange(fieldNum(FINCreditLine, DocumentState)).value(SysQuery::value(VersioningDocumentState::Approved));
        qbdsFINCreditLine.addRange(fieldNum(FINCreditLine, DocumentState)).value(SysQuery::value(VersioningDocumentState::Draft));
    }
    else
    {
        qbdsFINCreditLine.addRange(fieldNum(FINCreditLine, DocumentState)).value(SysQuery::valueNot(VersioningDocumentState::Finalized));
    }
}

public void clicked()
{
    //super();
    Args args = new Args();
    args.caller(element);
    args.parmObject(FINCreditInvoiceJournalReference::newInvoiceJournal(CustInvoiceJour.RecId));
    args.record(CustInvoiceJour.salesTable());
    this.menufunction().run(args);
}

public void clicked()
{
    // This is a temporary solution!
    // Invoicing only considers credits that are in the approved state, but we still want to calculate values for credits that are in the draft state
    // We therefore 'hack' the value and reset if after the pro forma has been posted or cancelled.
    VersioningDocumentState tmpDocumentSate = FINCreditLine.DocumentState;
    creditFormClass.approveCredit(CustInvoiceJour.TableId, CustInvoiceJour.RecId);

    //super();
    Args args = new Args();
    args.caller(element);
    args.parmObject(FINCreditInvoiceJournalReference::newInvoiceJournal(CustInvoiceJour.RecId));
    args.record(CustInvoiceJour.salesTable());
    this.menufunction().run(args);

    creditFormClass.setCreditState(CustInvoiceJour.TableId, CustInvoiceJour.RecId, tmpDocumentSate);
}

public void clicked()
{
    super();

    creditFormClass.approveCredit(CustInvoiceJour.TableId, CustInvoiceJour.RecId);
    CustInvoiceJour_ds.research(true);
}

public void clicked()
{
    super();

    creditFormClass.rejectCredit(CustInvoiceJour.TableId, CustInvoiceJour.RecId);
    CustInvoiceJour_ds.research(true);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    CreditStatusFilter.enabled(!this.value());
    FINCreditLine_ds.executeQuery();

    return ret;
}

public int selectionChange()
{
    int ret;

    ret = super();

    FINCreditLine_ds.executeQuery();

    return ret;
}

void clicked()
{            

    super();

    if (FINCreditLineDetails)
    {
        creditFormClass.deleteMultipleCreditParmLineDetails(FINCreditLineDetails, FINCreditLine);
    }

    FINCreditLine_ds.research(true);
    CustInvoiceJour_ds.refresh();            

}
