[Form]
public class FINSalesTotals extends FormRun
{
    SalesTotals             salesTotals;
    CustCreditLimit         custCreditLimit;
    SalesUpdate             salesUpdate;
    SalesParmTable          salesParmTable;
    SalesBasket             salesBasket;
    SalesLine               salesLine;
    FINSalesTotals          finSalesTotals;

}

public void  init()
{
    finSalesTotals = element.args().caller() as FINSalesTotals;

    super();

    if (element.args().parmEnumType())
    {
        salesUpdate = element.args().parmEnum();
        selectionUpdate.selection(enum2int(salesUpdate));
    }

    if (finSalesTotals)
    {
        selectionUpdate.selection(enum2int(finSalesTotals.specQty()));
        salesUpdate = selectionUpdate.selection();
    }

    switch(element.args().record().TableId)
    {
        case tableNum(SalesTable)       :
            break;

        case tableNum(SalesLine)       :
            salesLine = element.args().record();
            break;           

        case tableNum(SalesParmTable)   :
            salesParmTable= element.args().record();
            /*selectionUpdate.allowEdit(false);
            selectionUpdate.skip(true);
            selectionUpdate.visible(false);*/
            quantitySelectionTabPage.visible(true);
            break;

        case tableNum(SalesBasket)   :
            salesBasket= element.args().record();
            selectionUpdate.allowEdit(false);
            selectionUpdate.skip(true);
            selectionUpdate.visible(false);
            quantitySelectionTabPage.visible(true);
            break;
    }
}

void  setDisplayFields()
{
    container  displayFields;
    Common     orderTable;
    Set selectedRecId = new Set(Types::Int64);

    if (salesParmTable.RecId)
    {
        orderTable = salesParmTable;
    }
    else if (salesBasket.RecId)
    {
        orderTable = salesBasket;
    }
    else if (salesLine.RecId)
    {
        orderTable = salesLine.salesTable();
        selectedRecId.add(salesLine.RecId);
    }
    else
    {
        orderTable = salesTable;
    }

    using (DNRTradeTotalsDisposableContext context = new DNRTradeTotalsDisposableContext())
    {
        context.DNRParmCalledFromForm(true); // The value is used in /Classes/DNRSalesTotals_SalesClass_Extension/Methods/construct
        context.DNRParmSelectedRecId(selectedRecId);
        displayFields = SalesTotals::displayFieldsServer(orderTable, salesUpdate, CurrencyCode.text());
    }       

    qty.realValue                   (conpeek(displayFields, TradeTotals::posQty()               ));
    if (#PdsCatchWeightEnabled)
    {
        pdsCWSalesQty.realValue     (conpeek(displayFields, TradeTotals::pdsCWPosQty()          ));
    }
    weight.realValue                (conpeek(displayFields, TradeTotals::posWeight()            ));
    volume.realValue                (conpeek(displayFields, TradeTotals::posVolume()            ));
    cashDisc.realValue              (conpeek(displayFields, TradeTotals::posCashDisc()          ));
    costValue.realValue             (conpeek(displayFields, TradeTotals::posCostValue()         ));
    contributionMargin.realValue    (conpeek(displayFields, TradeTotals::posContributionMargin()));
    contributionRatio.realValue     (conpeek(displayFields, TradeTotals::posContributionRatio() ));
    currencyCode.text               (conpeek(displayFields, TradeTotals::posCurrency()          ));
    exchRate.realValue              (conpeek(displayFields, TradeTotals::posExchRate()          ));
    creditMax.realValue             (conpeek(displayFields, TradeTotals::posCreditMax()         ));
    freeValue.realValue             (conpeek(displayFields, conLen(displayFields)               ));
    lineDisc.realValue              (conpeek(displayFields, TradeTotals::posLineDisc()          ));
    endDisc.realValue               (conpeek(displayFields, TradeTotals::posEndDisc()           ));
    sumLines.realValue              (conpeek(displayFields, TradeTotals::posBalance()           ));
    sumMarkup.realValue             (conpeek(displayFields, TradeTotals::posMarkup()            ));

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        // include coupon
        sumMarkup.realValue         (sumMarkup.realValue()
                                    + conpeek(displayFields, TradeTotals::mcrPostotalCoupon())    );
        // set coupon value
        MCRSumMarkupCoupon.realValue     (conpeek(displayFields, TradeTotals::mcrPostotalCoupon()));
    }

    sumTax.realValue                (conpeek(displayFields, TradeTotals::posTaxTotal()          ));
    invoiceRoundOff.realValue       (conpeek(displayFields, TradeTotals::posRoundOff()          ));
    invoiceAmount.realValue         (conpeek(displayFields, TradeTotals::posTotalAmount()       ));

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        icmsAmount_BR       .realValue       (conpeek(displayFields, TradeTotals::posICMSTotal_BR()         ));
        icmsStAmount_BR     .realValue       (conpeek(displayFields, TradeTotals::posICMSSTTotal_BR()       ));
        ipiAmount_BR        .realValue       (conpeek(displayFields, TradeTotals::posIPITotal_BR()          ));
        pisAmount_BR        .realValue       (conpeek(displayFields, TradeTotals::posPISTotal_BR()          ));
        cofinsAmount_BR     .realValue       (conpeek(displayFields, TradeTotals::posCOFINSTotal_BR()       ));
        irrfAmount_BR       .realValue       (conpeek(displayFields, TradeTotals::posIRRFTotal_BR()         ));
        inssAmount_BR       .realValue       (conpeek(displayFields, TradeTotals::posINSSTotal_BR()         ));
        issAmount_BR        .realValue       (conpeek(displayFields, TradeTotals::posISSTotal_BR()          ));
        retainedINSS_BR     .realValue       (conpeek(displayFields, TradeTotals::posRetainedINSSTotal_BR() ));

        FreightAmount_BR       .realValue    (conpeek(displayFields, TradeTotals::posMarkupFreight_BR()     ));
        InsuranceAmount_BR     .realValue    (conpeek(displayFields, TradeTotals::posMarkupInsurance_BR()   ));
        OtherChargesAmoount_BR .realValue    (conpeek(displayFields, TradeTotals::posMarkupOther_BR()       ));

    }
    // </GBR>
}

public int  selectionChange()
{
    int  ret;

    ret = super();

    if (finSalesTotals)
    {
        finSalesTotals.specQty(selectionUpdate.selection());
        // remove previously calculated data
        finSalesTotals.cleanupPrecalculatedData();
        finSalesTotals.prepareDataForTotals();
        salesParmTable = finSalesTotals.getParmTable();
    }
    salesUpdate = selectionUpdate.selection();
    element.setDisplayFields();

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.setDisplayFields();

    return ret;
}
