public class FINVendEditInvoiceTotals extends FormRun 
// Copy of VendEditInvoiceTotal except linkActive() and init()
// Also filter control was added
{
    PurchTotals purchTotals;
    VendIInvoiceTotals vendIInvoiceTotals;
    FINPurchTotals finPurchTotals;
    PurchLine callerPurchLine;

}

public void calcTotals(boolean _updateTable = false, boolean _keepEndDisc = false)
{       

    //miscChargesBtn.visible(false);
    resetButton.visible(false);

    // <GBR>
    if (BrazilParameters::isEnabled() && vendInvoiceInfoTable.isForeignerVendor_BR())
    {
        additionButton_BR.visible(true);
    }
    else
    {
        additionButton_BR.visible(false);
    }
    // </GBR>

    if (!vendInvoiceInfoTable && finPurchTotals.getParmTable())
    {
        vendInvoiceInfoTable = finPurchTotals.getParmTable();
    }

    if (vendInvoiceInfoTable)
    {
        // If user is modifying amounts, remove taxes so they get recalculated
        if (_updateTable)
        {
            TaxUncommitted::deleteForDocumentHeader(vendInvoiceInfoTable.TableId, vendInvoiceInfoTable.RecId);
        }

        using (DNRTradeTotalsDisposableContext context = new DNRTradeTotalsDisposableContext())
        {
            context.DNRParmCalledFromForm(true); // The value is used in /Classes/DNRPurchTotalsClass_Extension/Methods/construct

            purchTotals = PurchTotals::newParmTable(vendInvoiceInfoTable, vendIInvoiceTotals.specQty(), vendInvoiceInfoTable.ParmId, '', vendInvoiceInfoTable.Ordering);
        }

        if (_keepEndDisc || VendInvoiceInfoTable.InvoiceType == PurchInvoiceType::VendorAdvance)
        {
            purchTotals.parmKeepEndDisc(true);
        }
        purchTotals.calc();

        if (purchTotals.allowEdit())
        {
            vendInvoiceInfoTable_ds.allowEdit(true);
            miscChargesBtn.visible(true);
            resetButton.visible(true);

            if (vendInvoiceInfoTable.Ordering == DocumentStatus::ApproveJournal)
            {
                purchParmTable_CashDisc.allowEdit(false);
                cashDiscDate.allowEdit(false);
            }

            vendInvoiceInfoTable.checkMarkupTrans();
        }
        else
        {
            vendInvoiceInfoTable_ds.allowEdit(false);
        }

        element.setDisplayFields(_updateTable);
    }        
}

/// <summary>
/// Disables the discount and miscellaneous charges fields when <c>VendInvoiceInfoTable</c> table has <c>InvoiceType</c> field with
/// <c>PurchInvoiceType::VendorAdvance</c> enum value.
/// </summary>
public void disableFieldsWhenAdvance()
{
    cashDiscDate.allowEdit(false);
    purchParmTable_CashDisc.allowEdit(false);
    endDisc.allowEdit(false);
    markup.allowEdit(false);
    miscChargesBtn.enabled(false);
}

public void init()
{
    vendIInvoiceTotals = element.args().caller() as VendIInvoiceTotals;
    if (vendIInvoiceTotals && vendIInvoiceTotals is FINPurchTotals)
    {
        finPurchTotals = vendIInvoiceTotals;
    }        

    super();   

    UpdateSelection.enumType(enumNum(PurchUpdate));

    if (finPurchTotals)
    {
        UpdateSelection.selection(enum2int(finPurchTotals.specQty()));
        element.initCalc();
        resetButton.visible(false);
        element.disableFieldsWhenAdvance();
        miscChargesBtn.enabled(true);
        cashDiscountAmount.allowEdit(false); 
        RoundOff.allowEdit(false);
    }
    else
    {
        UpdateSelection.visible(false);
    }
}

public void initCalc()
{
    element.calcTotals(false); // Calc totals to display, but do not udpdate the table until changes are made by the user
    /*
    // Hide title when there are no records
    mainInstructionStaticText.visible(vendInvoiceInfoTable_ds.numberOfRowsLoaded() > 0);

    // Set static texts
    // <Invoice Number> : <Invoice Account Name>
    str textSeparator = !vendInvoiceInfoTable.Num || vendInvoiceInfoTable.Num == "" || !vendInvoiceInfoTable.PurchName || vendInvoiceInfoTable.PurchName == "" ? "" : " : ";
    mainInstructionStaticText.text(strFmt("%1%2%3", vendInvoiceInfoTable.Num, textSeparator, vendInvoiceInfoTable.PurchName));
    */
    if (vendInvoiceInfoTable.isAdvance())
    {
        element.disableFieldsWhenAdvance();
    }
}

