public void setTableFields()
{
    container displayFields = purchTotals.displayFields([PurchTotals::posCashDisc(),
                                                          PurchTotals::posCashDiscDate(),
                                                          PurchTotals::posEndDisc(),
                                                          PurchTotals::posTaxRoundOff(),
                                                          PurchTotals::posRoundOff()]);

    vendInvoiceInfoTable.CashDisc = conPeek(displayFields, PurchTotals::posCashDisc());
    vendInvoiceInfoTable.CashDiscDate = conPeek(displayFields, PurchTotals::posCashDiscDate());
    vendInvoiceInfoTable.EndDisc = conPeek(displayFields, PurchTotals::posEndDisc());
    vendInvoiceInfoTable.TaxRoundOff = conPeek(displayFields, PurchTotals::posTaxRoundOff());
    vendInvoiceInfoTable.InvoiceRoundOff = conPeek(displayFields, PurchTotals::posRoundOff());

    vendInvoiceInfoTable.ReCalculate = NoYes::No;
}

public void close()
{
    super();

    if (finPurchTotals)
    {
        finPurchTotals.cleanupPrecalculatedData();
    }
}

public edit RoundOff editInvoiceRoundOff(boolean set, VendInvoiceInfoTable _vendInvoiceInfoTable, RoundOff _invoiceRoundOff)
{
    if (set)
    {
        element.setTableFields();
        _vendInvoiceInfoTable.InvoiceRoundOff = _invoiceRoundOff;
        _vendInvoiceInfoTable.modifiedField(fieldnum(VendInvoiceInfoTable, InvoiceRoundOff));

        element.calcTotals(true); // Calc totals and udpdate the table with changes made by the user
    }

    if (purchTotals)
    {
        return purchTotals.purchRoundOff();
    }

    return 0.0;
}

public edit VendCashDiscDate editCashDiscDate(boolean set, VendInvoiceInfoTable _vendInvoiceInfoTable, VendCashDiscDate _cashDiscDate)
{
    if (set)
    {
        element.setTableFields();
        _vendInvoiceInfoTable.CashDiscDate = _cashDiscDate;
        _vendInvoiceInfoTable.modifiedField(fieldnum(VendInvoiceInfoTable, CashDiscDate));

        element.calcTotals(true); // Calc totals and udpdate the table with changes made by the user
    }

    if (purchTotals)
    {
        return purchTotals.purchCashDiscDate();
    }

    return dateNull();
}

public edit VendCashDiscAmount editCashDisc(boolean set, VendInvoiceInfoTable _vendInvoiceInfoTable, VendCashDiscAmount _cashDisc)
{
    if (set)
    {
        element.setTableFields();
        _vendInvoiceInfoTable.CashDisc = _cashDisc;
        _vendInvoiceInfoTable.modifiedField(fieldnum(VendInvoiceInfoTable, CashDisc));

        element.calcTotals(true); // Calc totals and udpdate the table with changes made by the user
    }

    if (purchTotals)
    {
        return purchTotals.purchCashDisc();
    }

    return 0.0;
