public edit PurchEndDiscAmount editEndDisc(boolean set, VendInvoiceInfoTable _vendInvoiceInfoTable, PurchEndDiscAmount _endDisc)
{
    if (set)
    {
        // User has changed the total discount
        _vendInvoiceInfoTable.EndDisc = _endDisc;

        // Set recalculate flag to Yes, since we want the other editable values to default from PO when endDisc changes
        // The recalculate flag will be set to No after totals are calculated (setDisplayFields)
        _vendInvoiceInfoTable.ReCalculate = NoYes::Yes;
        _vendInvoiceInfoTable.modifiedField(fieldnum(VendInvoiceInfoTable, EndDisc));

        element.calcTotals(true, true); // Calc totals, udpdate the table with changes made by the user, and keep this field's value
    }

    if (purchTotals)
    {
        return purchTotals.purchEndDisc();
    }

    return 0.0;
}

void write()
{
    FormDataSource purchParmTable_dataSource;

    super();

    // call on calcTotals as the super() may have deleted the TaxUncommitteds.
    element.calcTotals();

    // Refresh data on parent form
    if (element.args() && element.args().record() && FormDataUtil::getFormDataSource(element.args().record()))
    {
        purchParmTable_dataSource = FormDataUtil::getFormDataSource(element.args().record());
        purchParmTable_dataSource.reread();
        purchParmTable_dataSource.refresh();
    }
}

void linkActive()
{
    super();

    element.initCalc();
}

/// <summary>
/// Checks for data specific situations when a field value in the <c>VendInvoiceInfoTable</c> Form Data Source changes.
/// </summary>
/// <param name="_fieldId">
/// The field identifier of the field being modified.
/// </param>
/// <param name="_unapprove">
/// true if the approveVariance should be called; otherwise false.
/// </param>
void modifiedField(fieldId _fieldId, boolean _unapprove)
{
    // Changes to total fields should only unapprove if using total matching
    if (_unapprove && VendParameters::find().UseTotalPriceMatching == NoYes::Yes)
    {
        vendInvoiceInfoTable.approveVariance(false);
    }
}

/// <summary>
///
/// </summary>
public void init()
{
    super();

    if (finPurchTotals)
    {
        VendInvoiceInfoTable_ds.executeQuery();
    }
}

/// <summary>
///
/// </summary>
public void executeQuery()
{
    super();

    if (finPurchTotals)
    {
        QueryBuildDataSource qbds = VendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable));// (element.args().record()
        SysQuery::findOrCreateRange(qbds, fieldNum(VendInvoiceInfoTable, RecId)).value(SysQuery::value(finPurchTotals.getParmTable().RecId));                
    }
}

int  selectionChange()
{
    int  ret;

    ret = super();

    if (finPurchTotals)
    {
        finPurchTotals.specQty(updateselection.selection());
        // remove previously calculated data
        finPurchTotals.cleanupPrecalculatedData();
        finPurchTotals.prepareDataForTotals();
        VendInvoiceInfoTable_ds.executeQuery();
        element.calcTotals();
    }

    return ret;
}

void clicked()
{
    NoYes originalRecalcValue = vendInvoiceInfoTable.ReCalculate;

    super();

    vendInvoiceInfoTable.initInvoiceTotals();

    vendInvoiceInfoTable_ds.write();

    element.calcTotals(false); // Calc totals to display, but do not udpdate the table until changes are made by the user

    // If the user had made changes, unapprove the invoice
    if (originalRecalcValue == NoYes::No)
    {
        vendInvoiceInfoTable_ds.modifiedField(0, true);
    }
}

void clicked()
{
    MenuFunction menuFunction;
    Args args = new Args();

    super();

    args.caller(element);
    args.record(finPurchTotals.getParmTable());//element.args().record());

    menuFunction = new MenuFunction(menuitemDisplayStr(MarkupTransInquiry), MenuItemType::Display);
    menuFunction.run(args);
}

