[Form]
public class FINVendInvoiceTransPeriods extends FormRun
{
    boolean                                     isProForma;
    FINRentalPeriodJournalizer_VendInvoiceJour  journalizer;
    PurchLine                                   purchLine;
    VendInvoiceTrans                            vendInvoiceTrans;
    FINInvoiceCalculatorParm                    invoiceCalculatorParm;

}

public void init()  
{
    if (!element.args().record())
    {
        throw error("@SYS22539");
    }

    if (element.args().dataset() == tableNum(VendInvoiceInfoLine))
    {
        VendInvoiceInfoLine vendInvoiceInfoLine = element.args().record();
        VendInvoiceJour     vendInvoiceJour; 

        purchLine = vendInvoiceInfoLine.purchLine();

        isProForma = true;

        invoiceCalculatorParm = FINInvoiceCalculatorParm::findParmLineRef(vendInvoiceInfoLine.TableId, vendInvoiceInfoLine.RecId);

        journalizer = FINRentalPeriodJournalizer::construct(vendInvoiceJour, invoiceCalculatorParm) as FINRentalPeriodJournalizer_VendInvoiceJour;
        journalizer.journalize();
    }
    else if (element.args().dataset() == tableNum(VendInvoiceTrans))
    {
        vendInvoiceTrans = element.args().record();
        purchLine = vendInvoiceTrans.purchLine();
    }

    super();
}

[SysClientCacheDataMethod]
public display PurchId displaySalesId()
{
    return purchLine.PurchId;
}

[SysClientCacheDataMethod]
public display TradeLineNumber displayLineNum()
{
    return purchLine.LineNumber;
}

[SysClientCacheDataMethod]
public display ItemId displayItemId()
{
    return purchLine.ItemId;
}

[SysClientCacheDataMethod]
public display BOBObjectId displayObjectId()
{
    return InventDim::find(purchLine.InventDimId).BOBDisplayObjectId();
}

[SysClientCacheDataMethod]
public display CurrencyCode displayCurrencyCode()
{
    return purchLine.CurrencyCode;
}

[SysClientCacheDataMethod]
public display VendInvoiceId displayInvoiceId()
{
    return vendInvoiceTrans.InvoiceId;
}

