public static container calculateTotalProposalForForm(FormDataSource _custInvoiceJourProFormaDS, CurrencyCode _currencyCode = '')
{
    AmountCur totalProposalAmount;
    AmountCur totalProposalAmountMST;
    CurrencyCode totalProposalCurrencyCode;
    boolean forceCurrencyCode;
    // If possible show total amount in common currency of the invoice proposals. If proposals have different currencies then show total amount in MST.
    boolean useCompanyCurrency = false;
    CurrencyCode companyCurrencyCode = Ledger::accountingCurrency();

    if (_currencyCode)
    {
        totalProposalCurrencyCode = _currencyCode;
        // It is possible to force recalculation of amount to a specific currency
        forceCurrencyCode = true;
    }

    if (_custInvoiceJourProFormaDS)
    {
        QueryRun                    queryRun = new QueryRun(_custInvoiceJourProFormaDS.query());
        FINCustInvoiceJourProForma  custInvoiceJourProForma = queryRun.get(tableNum(FINCustInvoiceJourProForma));
        AmountCur                   proposalAmount;
        AmountCur                   proposalAmountMST;
        CurrencyCode                proposalCurrencyCode;

        [proposalAmount, proposalAmountMST, proposalCurrencyCode] = custInvoiceJourProForma.getProposalAmounts();

        if (!totalProposalCurrencyCode)
        {
            totalProposalCurrencyCode = proposalCurrencyCode;
        }

        if (forceCurrencyCode && totalProposalCurrencyCode != proposalCurrencyCode)
        {
            proposalAmount = CurrencyExchangeHelper::curAmount2CurAmount(proposalAmount, proposalCurrencyCode, totalProposalCurrencyCode, custInvoiceJourProForma.InvoiceDate);
        }

        totalProposalAmount += proposalAmount;
        totalProposalAmountMST += proposalAmountMST;

        while (queryRun.next())
        {                
            if (queryRun.changed(tableNum(FINCustInvoiceJourProForma)))
            {
                custInvoiceJourProForma = queryRun.get(tableNum(FINCustInvoiceJourProForma));

                [proposalAmount, proposalAmountMST, proposalCurrencyCode] = custInvoiceJourProForma.getProposalAmounts();

                // If summed up proposals have different curriencies then use MST unless the amount needs to be displayed in a specific currency.
                if (!forceCurrencyCode && totalProposalCurrencyCode != proposalCurrencyCode)
                {
                    useCompanyCurrency = true;
                }

                if (!useCompanyCurrency && forceCurrencyCode && totalProposalCurrencyCode != proposalCurrencyCode)
                {
                    proposalAmount = CurrencyExchangeHelper::curAmount2CurAmount(proposalAmount, proposalCurrencyCode, totalProposalCurrencyCode, custInvoiceJourProForma.InvoiceDate);
                }

                totalProposalAmount += proposalAmount;
                totalProposalAmountMST += proposalAmountMST;
            }
        }
    }

    if (useCompanyCurrency)
    {
        totalProposalAmount = totalProposalAmountMST;
        totalProposalCurrencyCode = companyCurrencyCode;
    }

    return [totalProposalAmount, totalProposalCurrencyCode];
}

private container getProposalAmounts()
{
    return [this.InvoiceAmount, this.InvoiceAmountMST, this.CurrencyCode];
}

public static Common copyFromToProforma(Common _commonFrom, Common _commonTo)
{
    SysDictTable    dictTable = new SysDictTable(_commonTo.TableId);
    SysDictField    dictField;
    FieldId         fieldId = dictTable.fieldNext(0);

    while (fieldId)
    {
        dictField = dictTable.fieldObject(fieldId);

        if (dictField.isSql() && !dictField.isSystem())
        {
            FieldName fieldName = dictField.name();
            FieldId fieldIdFrom = fieldName2Id(_commonFrom.TableId, fieldName);

            if (fieldIdFrom)
            {
                _commonTo.(dictField.id()) = _commonFrom.(fieldIdFrom);
            }
        }

        fieldId = dictTable.fieldNext(fieldId);
    }

    return _commonTo;
}

public void approveCurrentVersion() 
{
    // Increments current version number to match correction if there is any.
    Version proformaVersion = FINManualPriceComponentProforma::getHighestVersionNumber(this.InvoiceId, this.SalesId);

    if (this.ProFormaInvoiceVersion <  proformaVersion)
    {
        ttsbegin;

        this.approveManualPriceToCharges();

        this.selectForUpdate(true);
        this.ProFormaInvoiceVersion = proformaVersion;
        this.update();

        FINCustInvoiceTransProForma custInvoiceTransProForma;

        update_recordset custInvoiceTransProForma
        setting ProFormaInvoiceVersion = proformaVersion
        where custInvoiceTransProForma.InvoiceId == this.InvoiceId
        &&    custInvoiceTransProForma.InvoiceDate == this.InvoiceDate
        &&    custInvoiceTransProForma.NumberSequenceGroup == this.NumberSequenceGroup
        &&    custInvoiceTransProForma.SalesId == this.SalesId;

        ttscommit;
    }
}

public boolean isInvoicePosted()
{
    boolean ret = false;
    CustInvoiceJour custInvoiceJour;

    select firstonly RecId from custInvoiceJour
    where  custInvoiceJour.InvoiceAccount == this.InvoiceAccount
    &&     custInvoiceJour.InvoiceId == this.InvoiceId
    &&     custInvoiceJour.LedgerVoucher == this.LedgerVoucher;

    if (custInvoiceJour)
    {
        ret = true;
    }

    return ret;
}

public CustTrans custTrans()
{
    CustTrans custTrans;

    select firstonly custTrans
    where custTrans.AccountNum  == this.InvoiceAccount
    &&    custTrans.TransDate   == this.InvoiceDate
    &&    custTrans.Invoice     == this.InvoiceId
    &&    custTrans.Voucher     == this.LedgerVoucher;

    return custTrans;
}

