public boolean validateMultiSelectPosting(FINCustInvoiceJournalProformaAction _action)
{
    boolean ret = true;
    boolean isInvoicePosted = this.IsProFormaInvoiceActive && this.isInvoicePosted();
    boolean isActiveNotPosted = this.IsProFormaInvoiceActive && !isInvoicePosted;
    boolean isActiveNotApproved = isActiveNotPosted && this.ProFormaInvoiceVersion < FINManualPriceComponentProforma::getHighestVersionNumber(this.InvoiceId, this.SalesId);

    if (isInvoicePosted)
    {
        ret = checkFailed(strFmt("@DNR:Placeholder12",
                                 strFmt("@DNR:FailedToProcessRentalInvoiceProposalX", this.InvoiceId),
                                 "@DNR:InvoiceProposalAlreadyPosted"));
    }

    if (ret && !this.IsProFormaInvoiceActive)
    {
        ret = checkFailed(strFmt("@DNR:Placeholder12",
                                 strFmt("@DNR:FailedToProcessRentalInvoiceProposalX", this.InvoiceId),
                                 "@DNR:InvoiceProposalNotActive"));
    }

    if (ret && _action == FINCustInvoiceJournalProformaAction::Post && isActiveNotApproved)
    {
        ret = checkFailed(strFmt("@DNR:Placeholder12",
                                 strFmt("@DNR:FailedToProcessRentalInvoiceProposalX", this.InvoiceId),
                                 "@DNR:InvoiceProposalNotApproved"));
    }

    return ret;
}

public static FINCustInvoiceJourProForma findRecId(RecId    _recId, boolean  _forUpdate = false)
{
    FINCustInvoiceJourProForma custInvoiceJourProForma;

    if (_recId)
    {
        custInvoiceJourProForma.selectForUpdate(_forUpdate);

        select firstonly custInvoiceJourProForma
        where custInvoiceJourProForma.RecId == _recId;
    }

    return custInvoiceJourProForma;
}

public static Set restoreMarkupForSalesOrder(RefRecId _parmProformaInvoiceJourRecId, SalesTable _salesTable)
{
    Set RecIdSet = new Set(Types::Int64);

    FINCustInvoiceJourProforma  custInvoiceJourProForma = FINCustInvoiceJourProforma::findRecId(_parmProformaInvoiceJourRecId);
    FINCustInvoiceTransProforma custInvoiceTransProforma;
    MarkupTrans markupTrans;
    SalesLine   salesLine;

    if (custInvoiceJourProForma)
    {
        while select markupTrans
        where markupTrans.TransRecId   == custInvoiceJourProForma.RecId
        &&    markupTrans.TransTableId == custInvoiceJourProForma.TableId
        &&    markupTrans.OrigTableId  == _salesTable.TableId
        &&    markupTrans.OrigRecId    == _salesTable.RecId
        &&    markupTrans.Keep         == NoYes::No
        &&   !markupTrans.IsDeleted
        {
            markupTrans.LineNum = MarkupTrans::lastLineNum(_salesTable.TableId, _salesTable.RecId) + 1;

            RecId recId = Markup::copySingleMarkupTrans(false, markupTrans, _salesTable.CurrencyCode, custInvoiceJourProForma, _salesTable, false, markupTrans).RecId;
            recIdSet.add(recId);
        }

        while select custInvoiceTransProforma
        where custInvoiceTransProForma.InvoiceId        == custInvoiceJourProForma.InvoiceId
        && custInvoiceTransProForma.InvoiceDate         == custInvoiceJourProForma.InvoiceDate
        && custInvoiceTransProForma.NumberSequenceGroup == custInvoiceJourProForma.NumberSequenceGroup
        && custInvoiceTransProForma.SalesId             == custInvoiceJourProForma.SalesId
            join SalesId from salesLine
            where salesLine.inventTransId == custInvoiceTransProForma.inventTransId
                join markupTrans
                where markupTrans.TransRecId   == custInvoiceTransProForma.RecId
                &&    markupTrans.TransTableId == custInvoiceTransProForma.TableId
                &&    markupTrans.OrigTableId  == salesLine.TableId
                &&    markupTrans.OrigRecId    == salesLine.RecId
                &&    markupTrans.Keep         == NoYes::No
                &&   !markupTrans.IsDeleted

        {
            markupTrans.LineNum = MarkupTrans::lastLineNum(salesLine.TableId, salesLine.RecId) + 1;

            RecId recId = Markup::copySingleMarkupTrans(false, markupTrans, _salesTable.CurrencyCode, custInvoiceTransProForma, salesLine, false, markupTrans).RecId;
            recIdSet.add(recId);
        }
    }

    return RecIdSet;
}

public void approveManualPriceToCharges()
{
    FINInvoiceCalcResult            calcResult;
    FINCustInvoiceTransProForma     custInvoiceTransProForma;
    FINManualPriceComponentProforma manualPriceComponentProforma;
    SalesTable                      salesTable;
    salesLine                       salesLine;
    MarkupTrans                     markupTrans;
    container                       cacheCon;
    RecordInsertList                insertList = new RecordInsertList(tableNum(MarkupTrans));
    Set                             recIdSet   = new Set(Types::Int64);
    Map                             lineNumMap = new Map(Types::Int64, Types::Real);

    // Add Manual Price component(correction) to the list of Charges of Invoice Proposal line
    while select RecId from custInvoiceTransProForma
    where custInvoiceTransProForma.InvoiceId           == this.InvoiceId
    &&    custInvoiceTransProForma.InvoiceDate         == this.InvoiceDate
    &&    custInvoiceTransProForma.NumberSequenceGroup == this.NumberSequenceGroup
    &&    custInvoiceTransProForma.SalesId             == this.SalesId
        join FINRentalConceptCode from salesTable
        where salesTable.SalesId == custInvoiceTransProForma.SalesId
            join salesLine
            where salesLine.InventTransid == custInvoiceTransProForma.InventTransId
                join  AmountCur from manualPriceComponentProforma
                where manualPriceComponentProforma.TransTableId             == custInvoiceTransProForma.TableId
                &&    manualPriceComponentProforma.TransRecId               == custInvoiceTransProForma.RecId
                &&    manualPriceComponentProforma.ProFormaInvoiceVersion   >  this.ProFormaInvoiceVersion

    {
        cacheCon   = FINInvoicePosting::getMarkupCodeCached(salesLine, FINRentalAccountType::Manual, salesTable.FINRentalConceptCode);
        calcResult = FINInvoiceCalcResult::construct(FINRentalAccountType::Manual, manualPriceComponentProforma.AmountCur);
        calcResult.initFromCacheContainer(cacheCon);

        markupTrans.clear();
        markupTrans.TransTableId = custInvoiceTransProForma.TableId;
        markupTrans.TransRecId   = custInvoiceTransProForma.RecId;

        if (lineNumMap.exists(markupTrans.TransRecId))
        {
            markupTrans.LineNum = lineNumMap.lookup(markupTrans.TransRecId) + 1;
        }
        else
        {
            markupTrans.LineNum = MarkupTrans::lastLineNum(markupTrans.TransTableId, markupTrans.TransRecId) + 1;
        }
        lineNumMap.insert(markupTrans.TransRecId, markupTrans.LineNum);

        markupTrans.FINInitFromInvoiceCalcResult(calcResult);
        markupTrans.FINInitFromRentalLine(salesLine);

        if (calcResult.isReversal && calcResult.markupTransRecId)
        {
            MarkupTrans markupTransInvoiced = markupTrans::findRecId(calcResult.markupTransRecId);
            markupTrans.TaxGroup = markupTransInvoiced.TaxGroup;
            markupTrans.TaxItemGroup = markupTransInvoiced.TaxItemGroup;
        }

        insertList.add(markupTrans);
    }

    if (insertList.insertDatabase() > 0)  // in case of any Manual Price component transfered we need to recalculate totals
    {
        while select custInvoiceTransProForma
        where custInvoiceTransProForma.InvoiceId           == this.InvoiceId
        &&    custInvoiceTransProForma.InvoiceDate         == this.InvoiceDate
        &&    custInvoiceTransProForma.NumberSequenceGroup == this.NumberSequenceGroup
        &&    custInvoiceTransProForma.SalesId             == this.SalesId
            join salesLine
            where salesLine.InventTransid == custInvoiceTransProForma.InventTransId
        {
            recIdSet = Set::union(recIdSet, this.copyMarkupTransForCustInvoiceTrans(salesLine, custInvoiceTransProForma));
        }

        SalesParmTable salesParmTable;

        select salesParmTable where salesParmTable.parmId == this.ParmId 
            && salesParmTable.SalesId == this.SalesId;

        SalesParmUpdate salesParmUpdate = salesParmTable.salesParmUpdate();

        SalesTotals salesTotals = SalesTotals::construct(salesParmTable, salesParmUpdate.SpecQty, AccountOrder::None, salesParmTable.ParmId, '', salesParmTable.Ordering);
        salesTotals.calc();

        this.initFromSalesTotals(salesTotals);
        this.calcMSTAmounts();
        this.update();

        FINMarkupTransGenerator::deleteMarkupTransForSet(recIdSet);
    }

}

void initFromSalesTotals(SalesTotals _salesTotals, AmountCur _cashDiscAmount = 0, boolean _installmentEligible = false)
{
    this.Qty    = _salesTotals.totalQty();
    this.Weight = _salesTotals.totalWeight();
    this.Volume = _salesTotals.totalVolume();
    this.SalesBalance = _salesTotals.totalBalance();
    this.EndDisc = _salesTotals.totalEndDisc();

    if (TaxParameters::find().CashDiscOnInvoice)
    {
        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceES00021, funcName());
        this.CashDisc = _cashDiscAmount;
    }
    else
    {
        this.CashDisc = _salesTotals.totalCashDisc();
    }

    this.SumMarkup = _salesTotals.totalMarkup();
    this.SumTax = _salesTotals.totalTaxAmount();
    this.SumLineDisc = _salesTotals.totalLineDisc();
    this.InvoiceRoundOff = _salesTotals.totalRoundOff();
    this.InvoiceAmount = _salesTotals.totalAmount();
    this.ReverseChargeAmount = _salesTotals.totalTaxReverseCharge();

    SalesTable salesTable = this.salesTable();
    if (RetailMCRChannelTable::mcrEnableOrderCompletion(salesTable))
    {
        AmountCur markupinstallment = _salesTotals.mcrReturnInstallmentEligibleMarkUpTotal();

        if (this.SumMarkup == markupinstallment
            && !_installmentEligible)
        {
            MCRSalesOrderType mcrSalesOrderType = salesTable.mcrEvalSalesOrderType(); 

            if (mcrSalesOrderType == MCRSalesOrderType::InstallmentOnly
                || mcrSalesOrderType == MCRSalesOrderType::OrderAndInstallment)
            {
                this.InvoiceAmount = this.InvoiceAmount - markupinstallment;
            }
        }
    }

    if (!TaxSolutionScopeIntegrationUtil::isCompanyEnabled() && TaxParameters::isCustomsEnable_IN())
    {
        this.InvoiceAmount = this.InvoiceAmount + _salesTotals.tax().getCustomsDuty_IN();
    }
}

AmountMST amountMST(AmountCur amountCur)
{
    return CurrencyExchangeHelper::mstAmount(amountCur, this.CurrencyCode, this.InvoiceDate, Currency::noYes2UnknownNoYes(this.Triangulation), this.ExchRate, this.ExchRateSecondary);
}

public void calcMSTAmounts()
{
    this.InvoiceAmountMST = this.amountMST(this.InvoiceAmount);
    this.InvoiceRoundOffMST = this.amountMST(this.InvoiceRoundOff);
    this.SumMarkupMST = this.amountMST(this.SumMarkup);
    this.SumLineDiscMST = this.amountMST(this.SumLineDisc);
    this.EndDiscMST = this.amountMST(this.EndDisc);
    this.SalesBalanceMST = this.amountMST(this.SalesBalance);
    this.SumTaxMST = this.amountMST(this.SumTax);
}

public Set copyMarkupTransForCustInvoiceTrans(SalesLine _salesLine, FinCustInvoiceTransProForma _custInvoiceTransProForma)
{
    MarkupTrans     oldMarkupTrans;
    Set RecIdSet = new Set(Types::Int64);

    while select oldMarkupTrans
    where oldMarkupTrans.TransTableId == _custInvoiceTransProForma.TableId &&
          oldMarkupTrans.TransRecId   == _custInvoiceTransProForma.RecId   &&
          !oldMarkupTrans.IsDeleted
    {
        recIdSet.add(Markup::copySingleMarkupTrans(false, oldMarkupTrans, _custInvoiceTransProForma.CurrencyCode, _custInvoiceTransProForma, _salesLine, false, oldMarkupTrans).RecId);
    }

    return recIdSet;

}
