[Form]
public class DNRRSHRentalShopDetails extends FormRun
{
    #define.emptyString('')

    boolean                          custSearchActive;
    RORRshInventDimCtrl_Frm_Editable inventDimFormSetup;
    Common                           callerRecord;
    RorRshShopCardId                 shopCardId;
    QueryBuildRange                  queryBuildRange;
    CustAccount                      custAccount;

}

public void init()
{
    if(element.args().record())
    {
        callerRecord = element.args().record();
        if(callerRecord.tableId == tableNum(RorRshShopCardTable))
        {
            RorRshShopCardTable     shopCardTable = callerRecord;

            shopCardId = shopCardTable.ShopCardId;
        }

        if(callerRecord.TableId == tableNum(CustTable))
        {
            CustTable       custTable = callerRecord;

            custAccount = custTable.AccountNum;
        }
    }

    super();

    this.updateDesignRentalShopTrans(InventDimFormDesignUpdate::Init);
    this.updateDesign();

}

public void setToDateTime()
{
    if (RorRshShopCardTable.FromDateTime)
    {
        RorRshShopCardTable.ToDateTime = RorRshShopCardTable.calculateToDateTime(RentalDuration.value());
        RorRshShopCardTable.ToDate = DateTimeUtil::date(RorRshShopCardTable.ToDateTime);
    }
}

private void setRentalDuration()
{
    if (RorRshShopCardTable.FromDate && RorRshShopCardTable.ToDate)
    {
        RentalDuration.value(RorRshShopCardTable.calculateRentalDuration());
    }
}

public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

void mcrRunCustomerSearch()
{
    AccountNum   custSearchAccount;
    CustTable    custTable;

    //if there is a search string
    if (mcrSearchText.text() && !custSearchActive)
    {
        custSearchActive = true;
        custSearchAccount = MCRCustSearch::searchDisplayForm(mcrSearchText.text(), mcrCustSearchType.selection(), true, element);
        custSearchActive = false;

        // if a customer was found
        if (custSearchAccount)
        {
            select firstonly custTable where custTable.AccountNum == custSearchAccount;
            custSearchAccount = strRem(custSearchAccount, ',');
            RorRshShopCardTable.CustAccount = custSearchAccount;
            Customer_CustAccount.text(custSearchAccount);
            Customer_CustAccount.modified();
        }
    }
}

public void updateDesign()
{
    boolean confirmAllowed = RorRshShopCardTable.CustAccount
                                && RorRshShopCardTable.FromDate
                                && RorRshShopCardTable.ToDate
                                && !RorRshShopCardTable.IsConfirmed;

    RorRshShopCardTable_ds.allowEdit(!RorRshShopCardTable.IsConfirmed);
    RorRshShopCardTable_ds.allowDelete(!RorRshShopCardTable.IsConfirmed);
    RorRshShopTrans_ds.allowEdit(!RorRshShopCardTable.IsConfirmed);
    RorRshShopTrans_ds.allowCreate(!RorRshShopCardTable.IsConfirmed);
    RorRshShopTrans_ds.allowDelete(!RorRshShopCardTable.IsConfirmed);
    RetailAddItemMenuItem.enabled(!RorRshShopCardTable.IsConfirmed);

    RORSalesTable.enabled(RorRshShopCardTable.SalesId != #emptyString);
    DNRRSHRentalShopConfirm.enabled(confirmAllowed);
    DNRRSHRentalRetailCheckout.enabled(RorRshShopCardTable.IsConfirmed);
}

public void updateDesignRentalShopTrans(InventDimFormDesignUpdate _mode)
{
    boolean                      emptyLine;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = RORRshInventDimCtrl_Frm_Editable::newFromFormItem(element, RorRshShopTrans.ItemId);
            }
        // Fall through

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange:
            emptyLine = RorRshShopTrans.ItemId == '';
            inventoryDimensionsGrid.allowEdit(!emptyLine);
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(RorRshShopTrans.ItemId));
            inventDimFormSetup.formSetControls(_mode == InventDimFormDesignUpdate::Active);
            element.enableLineAssignmentActions();
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public void refreshInventDimDatasource()
{
    InventDim.data(RorRshShopTrans.inventDim());
    InventDim_DS.setCurrent();
}

public void modifyInventDimField(FieldId _fieldId)
{
    if (RorRshShopTrans.modifyInventDim(inventDim, _fieldId))
    {
        element.refreshInventDimDatasource();
    }
}

public void close()
{
    if (!RorRshShopCardTable.CustAccount || !RorRshShopCardTable.ToDateTime)
    {
        if (Box::yesNo("@DNR:SomeDetailsMissing", DialogButton::No) == DialogButton::No)
        {
            RorRshShopCardTable.delete();
            delete_from RorRshShopTrans
                where RorRshShopTrans.ShopCardId == RorRshShopCardTable.ShopCardId;
        }
    }

    super();
}

public void enableLineAssignmentActions()
{
    boolean isRental = (RorRshShopTrans.TradeType == RORTradeType::Rental || RorRshShopTrans.TradeType == RORTradeType::SubRental);
    boolean canAssignObject;

    if (isRental)
    {
        BOBRentalType rentalType;
        EcoResProduct product;
        InventTable   inventTable;

        select RecId, Classification from rentalType
            join TableId from product
            where product.BOBRentalTypeId == rentalType.RentalTypeId
                join TableId from inventTable
                where inventTable.Product == product.RecId
                &&    inventTable.ItemId == RorRshShopTrans.ItemId;

        canAssignObject = (rentalType.RecId && rentalType.Classification == BOBRentalTypeClassification::Serialized && !RorRshShopCardTable.IsConfirmed);
    }
    BOBObjectAssignmentEngine.enabled(canAssignObject);
}

private void setWareHouse()
{
    RorRshShopCardTable.LocationId = RorRshShopCardTable::getRentalCounterInventLocationId();
    RorRshShopCardTable.modifiedField(fieldNum(RorRshShopCardTable, LocationId));
}

public void init()
{
    super();
    queryBuildRange = this.query().dataSourceTable(tableNum(RorRshShopCardTable)).addRange(fieldNum(RorRshShopCardTable, ShopCardId));
    this.query().dataSourceTable(tableNum(RorRshShopCardTable)).addRange(fieldNum(RorRshShopCardTable, RentalShopType)).value(queryValue(DNRRSHRentalShopType::Customer));
}

public void executeQuery()
{
    if(queryBuildRange && shopCardId)
    {
        queryBuildRange.value(shopCardId);
    }
    super();
}

public void initValue()
{
    Timezone timeZone;
    super();

    RorRshShopCardTable.CustAccount    = custAccount;
    RorRshShopCardTable.RentalShopType = DNRRSHRentalShopType::Customer;
    RorRshShopCardTable.FromDateTime   = DateTimeUtil::getSystemDateTime();
    timeZone = DateTimeUtil::getOriginatingTimeZone(RorRshShopCardTable.FromDateTime);
    RorRshShopCardTable.FromDate       = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(RorRshShopCardTable.FromDateTime, timeZone));
    RorRshShopCardTable.Active         = NoYes::Yes;
    RentalDuration.value(0);
    element.setWareHouse();            
}

public int active()
{
    int ret;

    ret = super();
    element.updateDesign();
    element.setRentalDuration();
    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(!RorRshShopCardTable.CustAccount || !RorRshShopCardTable.ToDateTime)
    {
        ret = ret && checkFailed("@DNR:SomeInformationMissing");
    }
    return ret;
}

