public void write()
{
    RorRshShopCardTable     shopCardTable_Orig = RorRshShopCardTable.orig();

    super();

    if(RorRshShopCardTable.ToDateTime != shopCardTable_Orig.ToDateTime)
    {
        RorRshShopCardTable::updateExpectedOffRentDateForRentalShopTrans(RorRshShopCardTable, RorRshShopCardTable.ToDateTime);
    }
}

