SourceCode.Declaration:
class DNRAppEmplNotificationCleanupService extends SysOperationServiceBase
{
    WPPParameters parameters;

}

Method.Source:
public void cleanup()
{
    ttsbegin;

    parameters = WPPParameters::find();

    this.cleanupNotifications();

    ttscommit;
}

protected void cleanupNotifications()
{
    TransDateTime cleanupTillDateTime = DateTimeUtil::addDays(DateTimeUtil::getSystemDateTime(), -parameters.DNRAppEmplRetentionDaysNotifications);
    DNRAppEmplNotificationTable appNotifications;

    delete_from appNotifications
    where appNotifications.CreatedDateTime < cleanupTillDateTime;
}
